import React, { Component } from 'react';
import './AddProductDialog.style.scss';
import { withCookies } from 'react-cookie';
import { connect } from 'react-redux';
import * as actions from '../../../../../redux/actions';
import { Button, Modal, Form, Input, InputNumber } from 'antd';
import { PlusOutlined } from '@ant-design/icons';
import ImageUploader from '../../../../utilities/ImageUploader/ImageUploader';

const layout = {
  labelCol: { span: 6 },
  wrapperCol: { span: 18 },
};

class AddProductDialog extends Component {
  constructor(props) {
    super(props);

    this.state = {
      isVisible: false
    }

    this.formRef = React.createRef();
  }

  setDialogVisible(isVisible) {
    this.setState({ isVisible });
  }

  async onOK() {

  }

  render() {
    const { selectedCategory } = this.props;
    return (
      <div className="product-management__add-product-dialog">
        <Button
          shape="circle"
          icon={<PlusOutlined />}
          className="product-management__add-product-dialog__btn-open"
          onClick={() => this.setDialogVisible(true)}
        />

        <Modal
          title={<span style={{ color: '#ff8220' }}>{`${selectedCategory.name} | Thêm sản phẩm mới`}</span>}
          centered
          visible={this.state.isVisible}
          onOk={() => this.onOK()}
          onCancel={() => this.setDialogVisible(false)}
          okText="Hoàn tất"
          cancelText="Hủy bỏ"
          okButtonProps={{ style: { background: '#ff8220', border: 0 } }}
        >
          <div className="product-management__add-product-dialog__content">
            <div className="product-management__add-product-dialog__content__img-uploading">
              <ImageUploader
                width={150}
                height={150}
                onFinish={imageUrl => console.log(imageUrl)}
              />
            </div>
            <Form
              {...layout}
              ref={current => { this.formRef.current = current; }}
              className="product-management__add-product-dialog__content__form"
            >
              <Form.Item
                name="name"
                label="Tên sản phẩm"
                rules={[{ required: true, message: 'Vui lòng nhập tên sản phẩm' }]}
              >
                <Input placeholder="Tối đa 50 kí tự" />
              </Form.Item>

              <Form.Item
                name="supplier"
                label="Nhà cung cấp"
                rules={[
                  {
                    required: true,
                    message: 'Vui lòng nhập tên nhà cung cấp'
                  }
                ]}
              >
                <Input placeholder="Tối đa 50 kí tự" />
              </Form.Item>

              <Form.Item
                name="price"
                label="Giá bán (VNĐ)"
                rules={[
                  {
                    required: true,
                    message: 'Vui lòng nhập giá bán'
                  }
                ]}
              >
                <InputNumber
                  placeholder="Tối thiểu là 0"
                  formatter={value => `${value}`.replace(/\B(?=(\d{3})+(?!\d))/g, ',')}
                  parser={value => value.replace(/\$\s?|(,*)/g, '')}
                />
              </Form.Item>

              <Form.Item
                name="availableQuantity"
                label="Số lượng hiện có"
                rules={[
                  {
                    required: true,
                    message: 'Vui lòng nhập số lượng hiện có'
                  }
                ]}
              >
                <InputNumber
                  placeholder="Tối thiểu là 0"
                  formatter={value => `${value}`.replace(/\B(?=(\d{3})+(?!\d))/g, ',')}
                  parser={value => value.replace(/\$\s?|(,*)/g, '')}
                />
              </Form.Item>

            </Form>
          </div>
        </Modal>
      </div>
    )
  }
}
export default connect(null, actions)(withCookies(AddProductDialog));
