import React from 'react';
import { Row, Col, Input, Empty, Button, Divider, List, message } from 'antd';
import { SearchOutlined, PlusOutlined, EditOutlined, DeleteOutlined } from '@ant-design/icons';
import './SupplierManagement.style.scss';
import { connect } from 'react-redux';
import { withCookies } from 'react-cookie';
import * as actions from '../../../../../redux/actions';
import PageBase from '../../../../utilities/PageBase/PageBase';
import { API } from '../../../../../constants/api.constant';
import { COOKIE_NAMES } from '../../../../../constants/cookie-name.constant';

let data = [
  'Racing car sprays burning fuel into crowd.',
  'Japanese princess to wed commoner.',
  'Australian walks 100km after outback crash.',
  'Man charged over missing wedding girl.',
  'Racing car sprays burning fuel into crowd.',
  'Japanese princess to wed commoner.',
  'Australian walks 100km after outback crash.',
  'Man charged over missing wedding girl.'
];

class SupplierManagement extends PageBase {
  constructor(props) {
    super(props);
    this.state = {
      suppliersDataSource: [],
      filteredSuppliers: [],
      selectedSupplier: {}
    }
  }

  componentDidMount() {
    this.loadSuppliers();
  }

  async loadSuppliers() {
    this.props.setAppLoading(true);
    const res = await (
      await fetch(
        API.Manager.SupplierManagment.getSuppliers,
        {
          method: 'GET',
          headers: {
            'Content-type': 'application/json; charset=UTF-8',
            'token': this.props.cookies.get(COOKIE_NAMES.token)
          },
          signal: this.abortController.signal
        }
      )
    ).json();

    this.props.setAppLoading(false);
    if (res.status !== 200) {
      message.error(res.errors[0]);
      return;
    }

    const { suppliers } = res.data;
    this.setState({
      suppliersDataSource: [...suppliers],
      filteredSuppliers: [...suppliers]
    });
  }

  render() {
    return (
      <div className="importing-request__container__suppliers">
        <div className="importing-request__container__suppliers__header">
          <span className="importing-request__container__suppliers__header__title">
            Nhà cung cấp sản phẩm
          </span>
        </div>
        <div className="importing-request__container__suppliers__toolbar">
          <Row style={{ width: '100%' }}>
            <Col span={21}>
              <Input
                prefix={<SearchOutlined style={{ marginRight: 5 }} />}
                placeholder="Tìm kiếm theo tên, địa chỉ, điện thoại..." />
            </Col>
            <Col span={3} align="center">
              <Button
                icon={<PlusOutlined />}
                className="importing-request__container__suppliers__toolbar__btn-add-supplier"
              />
            </Col>
          </Row>
        </div>
        <div className="importing-request__container__suppliers__list">
          <List
            size="small"
            dataSource={data}
            renderItem={item => <List.Item>{item}</List.Item>}
            locale={{ emptyText: <Empty image={Empty.PRESENTED_IMAGE_SIMPLE} description="Chưa có dữ liệu" /> }}
          />
        </div>
        <div className="importing-request__container__suppliers__details">
          <div className="importing-request__container__suppliers__details__header">
            <span className="importing-request__container__suppliers__details__header__title">
              Thông tin liên hệ
                    </span>
            <div className="importing-request__container__suppliers__details__header__toolbar">
              <Button
                shape="circle"
                icon={<EditOutlined />}
                className="importing-request__container__suppliers__details__header__toolbar__btn"
              />

              <Button
                shape="circle"
                icon={<DeleteOutlined />}
                className="importing-request__container__suppliers__details__header__toolbar__btn"
              />
            </div>
          </div>
          <Divider />
          <div className="importing-request__container__suppliers__details__content">
            <Row style={{ width: '100%' }}>
              <Col span={6}>
                <span className="importing-request__container__suppliers__details__content__label">
                  Thương hiệu
                        </span>
              </Col>
              <Col span={18}>Vinamilk</Col>
            </Row>
            <Row style={{ width: '100%' }}>
              <Col span={6}>
                <span className="importing-request__container__suppliers__details__content__label">
                  Số điện thoại
                        </span>
              </Col>
              <Col span={18}>0999999999</Col>
            </Row>
            <Row style={{ width: '100%' }}>
              <Col span={6}>
                <span className="importing-request__container__suppliers__details__content__label">
                  Địa chỉ
                        </span>
              </Col>
              <Col span={18}>31 Võ Văn Tần, Phường 6, Quận 3, TP.HCM</Col>
            </Row>

            <iframe
              title="supplier map"
              src="https://www.google.com/maps/embed?pb=!1m18!1m12!1m3!1d3919.4354306193154!2d106.68967831525073!3d10.77792426211209!2m3!1f0!2f0!3f0!3m2!1i1024!2i768!4f13.1!3m3!1m2!1s0x31752f3a6fbd987f%3A0xe6553f3f2872c3db!2zMzEgVsO1IFbEg24gVOG6p24sIFBoxrDhu51uZyA2LCBRdeG6rW4gMywgSOG7kyBDaMOtIE1pbmgsIFZp4buHdCBOYW0!5e0!3m2!1svi!2s!4v1586275303962!5m2!1svi!2s" width={600} height={450} frameBorder={0} style={{ border: 0 }} allowFullScreen aria-hidden="false" tabIndex={0} />

          </div>
        </div>
      </div>
    )
  }
}
export default connect(null, actions)(withCookies(SupplierManagement));
