import React, { Component } from 'react';
import { Card, Modal, List, Avatar, Input, Radio, Row, Col } from 'antd';
import { SearchOutlined } from '@ant-design/icons';
import { withCookies } from 'react-cookie';
import './WorkScheduleReport.style.scss';
import USER_ROLES from '../../../../../constants/user-role.constant';

const { Meta } = Card;

class WorkScheduleReport extends Component {
  constructor(props) {
    super(props);

    this.state = {
      isVisible: false,
      filteredStaffs: []
    }
  }

  setDialogVisible(isVisible) {
    if (isVisible) {
      this.setState({ filteredStaffs: [...this.props.staffs] })
    }

    this.setState({ isVisible });
  }

  printReport() {

  }

  render() {
    const { basicInfo, index } = this.props;
    const { filteredStaffs } = this.state;

    return (
      <div>
        <Card
          className={`reporting__menu__item animated fadeInUp`}
          style={{ width: '100%', animationDuration: `${0.5 * (index + 1)}s` }}
          cover={
            <img
              alt="example"
              src={basicInfo.cover}
            />
          }
          onClick={() => this.setDialogVisible(true)}
        >
          <Meta
            title={basicInfo.title}
            description={basicInfo.description}
          />
        </Card>

        <Modal
          title={basicInfo.title}
          visible={this.state.isVisible}
          onOk={() => this.printReport()}
          okButtonProps={{ style: { background: '#ff8220', border: 0 } }}
          onCancel={() => this.setDialogVisible(false)}
          cancelButtonProps={{ style: { display: 'none' } }}
          okText="In báo cáo"
        >
          <div className="reporting__work-schedule-report">
            <div className="reporting__work-schedule-report__list-staffs">

              <Input prefix={<SearchOutlined />} placeholder="Tìm kiếm nhân viên..." />

              <div className="reporting__work-schedule-report__list-staffs__wrapper">
                {/* <List
                  itemLayout="horizontal"
                  dataSource={filteredStaffs}
                  renderItem={staff => (
                    <List.Item>
                      <List.Item.Meta
                        avatar={}
                        title={staff.fullname}
                        description={USER_ROLES[staff.role].name}
                      />
                    </List.Item>
                  )}
                /> */}

                <Radio.Group style={{ width: '100%' }}>
                  {filteredStaffs.map(staff => (
                    <Radio value={staff._id} style={{ width: '100%' }}I>
                      <Row style={{ width: '100%' }}>
                        <Col span={2}><Avatar src={staff.avatar} /></Col>
                        <Col span={22}>
                          <div>
                            <div><span>{staff.fullname}</span></div>
                            <div><span>{USER_ROLES[staff.role].name}</span></div>
                          </div>
                        </Col>
                      </Row>
                    </Radio>
                  ))}
                </Radio.Group>

              </div>
            </div>
          </div>
        </Modal>

      </div>
    )
  }
}

export default withCookies(WorkScheduleReport);
