import React, { Component } from 'react';
import { PlusOutlined } from '@ant-design/icons';
import { Tooltip, Button, Modal, Form, Input, Select } from 'antd';
import './AddStaffDialog.style.scss';
import USER_SEX from '../../../../../constants/user-sex';

const { Option } = Select;

const layout = {
  labelCol: { span: 8 },
  wrapperCol: { span: 16 },
};

const tailLayout = {
  wrapperCol: { offset: 8, span: 16 },
};

class AddStaffDialog extends Component {
  constructor(props) {
    super(props);

    this.state = {
      isVisible: false
    }
  }

  setDialogVisible(isVisible) {
    this.state({ isVisible });
  }

  onOk() {
    this.setDialogVisible(false);
  }

  onCancel() {
    this.setDialogVisible(false);
  }

  render() {
    return (
      <div className="staff-management__body__staffs__content__list-staffs__header__add-staff-dialog">
        <Tooltip placement="bottom" title="Thêm nhân viên">
          <Button
            className="staff-management__body__staffs__content__list-staffs__header__add-staff-dialog__btn-open"
            shape="circle"
            type="link"
            icon={<PlusOutlined />}
            onClick={() => this.setDialogVisible(true)}
          />
        </Tooltip>

        <Modal
          title="Thêm nhân viên mới"
          centered
          visible={this.state.isVisible}
          onOk={() => this.onOk()}
          onCancel={() => this.onCancel()}
        >

          <Form {...layout}>
            <Form.Item
              name="fullname"
              label="Họ và tên"
              rules={[
                {
                  required: true,
                  message: 'Vui lòng nhập họ và tên'
                }
              ]}
            >
              <Input />
            </Form.Item>

            <Form.Item
              name="sex"
              label="Giới tính"
              rules={[
                {
                  required: true,
                  message: 'Vui lòng chọn giới tính'
                }
              ]}
            >
              <Select
                placeholder="Giới tính"
                allowClear
              >
                <Option value={USER_SEX.MALE.type}>{USER_SEX.MALE.name}</Option>
                <Option value={USER_SEX.FEMALE.type}>{USER_SEX.FEMALE.name}</Option>
              </Select>
            </Form.Item>

          </Form>

        </Modal>
      </div>
    )
  }
}
export default AddStaffDialog;
