import React, { Component } from 'react';
import { Row, Col } from 'antd';
import NumberFormat from 'react-number-format';
import './BillToPrint.style.scss';

export default class BillToPrint extends Component {
  render() {
    const { checkoutSession } = this.props;

    return (
      <div className="money-counting__submit-checkout-session-dialog__bill">
        <div className="money-counting__submit-checkout-session-dialog__bill__company">
          <img className="money-counting__submit-checkout-session-dialog__bill__company__logo" src={require('../../../../../../assets/images/app-logo.png')} alt="logo" />
          <div className="money-counting__submit-checkout-session-dialog__bill__company__brand">
            <div className="money-counting__submit-checkout-session-dialog__bill__company__brand__name"><span>Mini Mart</span></div>
            <div className="money-counting__submit-checkout-session-dialog__bill__company__brand__slogan"><span>Tiện Lợi mà Chất Lượng</span></div>
          </div>
        </div>
        <ul className="money-counting__submit-checkout-session-dialog__bill__list-items">
          <li style={{ fontWeight: 'bold' }}>
            <Row>
              <Col span={14}>SP</Col>
              <Col span={4}>SL</Col>
              <Col span={6}>TT</Col>
            </Row>
          </li>
          {checkoutSession.soldItems.map(item => (
            <li key={item._id}>
              <Row>
                <Col span={14}>{item.product.name}</Col>
                <Col span={4}>x{item.quantity}</Col>
                <Col span={6}>
                  <NumberFormat
                    value={Number(item.product.price * item.quantity)}
                    displayType="text"
                    thousandSeparator={true}
                    suffix=" VNĐ"
                  />
                </Col>
              </Row>
            </li>
          ))}
        </ul>
      </div>
    )
  }
}
