import React, { Component } from 'react';
import { PlusOutlined, UserAddOutlined } from '@ant-design/icons';
import { Tooltip, Button, Modal, Form, Input, Select, DatePicker, Row, Col, Divider } from 'antd';
import './AddStaffDialog.style.scss';
import USER_SEX from '../../../../../constants/user-sex';

const { Option } = Select;
const layout = {
  labelCol: { span: 6 },
  wrapperCol: { span: 18 },
};

class AddStaffDialog extends Component {
  constructor(props) {
    super(props);

    this.state = {
      isVisible: false
    }
  }

  setDialogVisible(isVisible) {
    this.setState({ isVisible });
  }

  onOk() {
    this.setDialogVisible(false);
  }

  onCancel() {
    this.setDialogVisible(false);
  }

  render() {
    return (
      <div className="staff-management__body__staffs__content__list-staffs__header__dialogs__add-staff-dialog">
        <Tooltip placement="bottom" title="Thêm nhân viên">
          <Button
            className="staff-management__body__staffs__content__list-staffs__header__dialogs__add-staff-dialog__btn-open"
            shape="circle"
            type="link"
            icon={<PlusOutlined />}
            onClick={() => this.setDialogVisible(true)}
          />
        </Tooltip>

        <Modal
          className="staff-management__body__staffs__content__list-staffs__header__dialogs__add-staff-dialog__content"
          title={
            <div>
              <UserAddOutlined style={{ color: '#ff8220', marginRight: 7 }} />
              <span style={{ color: '#ff8220' }}>Thêm nhân viên mới</span>
            </div>
          }
          centered
          visible={this.state.isVisible}
          onOk={() => this.onOk()}
          onCancel={() => this.onCancel()}
          okText="Hoàn tất"
          okButtonProps={{ style: { background: '#ff8220', borderColor: '#ff8220' } }}
        >

          <Form
            {...layout}
            className="staff-management__body__staffs__content__list-staffs__header__dialogs__add-staff-dialog__form">
            <Row gutter={10}>
              <Col span={10}>
                <h3>Thông tin cá nhân</h3>
                <Form.Item
                  label="Họ và tên"
                  name="fullname"
                  rules={[
                    {
                      required: true,
                      message: 'Vui lòng nhập họ và tên'
                    }
                  ]}
                >
                  <Input placeholder="Tối đa 30 kí tự" />
                </Form.Item>

                <Form.Item
                  label="Giới tính"
                  name="sex"
                  rules={[
                    {
                      required: true,
                      message: 'Vui lòng chọn giới tính'
                    }
                  ]}
                >
                  <Select
                    placeholder="Chọn giới tính"
                    allowClear
                  >
                    <Option value={USER_SEX.MALE.type}>{USER_SEX.MALE.name}</Option>
                    <Option value={USER_SEX.FEMALE.type}>{USER_SEX.FEMALE.name}</Option>
                  </Select>
                </Form.Item>

                <Form.Item
                  label="Ngày sinh"
                  name="dateOfBirth"
                  rules={[
                    {
                      required: true,
                      message: 'Vui lòng chọn ngày sinh'
                    }
                  ]}
                >
                  <DatePicker placeholder="Chọn ngày sinh" format="DD-MM-YYYY" />
                </Form.Item>

                <Form.Item
                  label="E-mail"
                  name="email"
                  rules={[
                    {
                      required: true,
                      type: "email",
                      message: 'Vui lòng nhập địa chỉ email'
                    }
                  ]}
                >
                  <Input placeholder="abc@gmail.com" />
                </Form.Item>

                <Form.Item
                  label="Điện thoại"
                  name="phone"
                  rules={[
                    {
                      required: true,
                      message: 'Vui lòng nhập số điện thoại'
                    }
                  ]}
                >
                  <Input placeholder="Gồm 10 hoặc 11 số" />
                </Form.Item>

                <Form.Item
                  label="Địa chỉ"
                  name="address"
                  rules={[
                    {
                      required: true,
                      message: 'Vui lòng nhập địa chỉ nơi ở hiện tại'
                    }
                  ]}
                >
                  <Input.TextArea placeholder="227 Nguyễn Văn Cừ, Quận 5" />
                </Form.Item>
              </Col>
              <Col span={2} style={{ textAlign: 'center' }}><Divider type="vertical" /></Col>
              <Col span={12}>
                <h3>Thông tin tài khoản</h3>
                <Form.Item
                  label="Tên tài khoản"
                  name="username"
                  rules={[
                    {
                      required: true,
                      message: 'Vui lòng nhập tên tài khoản'
                    }
                  ]}
                >
                  <Input placeholder="Tối đa 30 kí tự" />
                </Form.Item>

                <Form.Item
                  label="Mật khẩu"
                  name="password"
                  rules={[
                    {
                      required: true,
                      message: 'Vui lòng nhập mật khẩu'
                    }
                  ]}
                >
                  <Input.Password placeholder="Bao gồm số, kí tự đặc biệt, hoa và thường" />
                </Form.Item>

                <Form.Item
                  label="Nhập lại mật khẩu"
                  name="confirmedPassword"
                  rules={[
                    {
                      required: true,
                      message: 'Vui lòng nhập lại mật khẩu'
                    }
                  ]}
                >
                  <Input.Password />
                </Form.Item>

              </Col>
            </Row>
          </Form>

        </Modal>
      </div>
    )
  }
}
export default AddStaffDialog;
