import React, { Component } from 'react';
import { Row, Col, Tabs, Select, Input, List, Avatar } from 'antd';
import { SearchOutlined } from '@ant-design/icons'
import './WorkAssignment.style.scss';

const { TabPane } = Tabs;
const { Option, OptGroup } = Select;

const tasks = [
  'Racing car sprays burning fuel into crowd.',
  'Japanese princess to wed commoner.',
  'Australian walks 100km after outback crash.',
  'Man charged over missing wedding girl.',
  'Los Angeles battles huge wildfires.',
];

const members = [
  {
    title: 'Ant Design Title 1',
  },
  {
    title: 'Ant Design Title 2',
  },
  {
    title: 'Ant Design Title 3',
  },
  {
    title: 'Ant Design Title 4',
  },
];

export default class WorkAssignment extends Component {
  render() {
    return (
      <div className="work-assignment">
        <Row>
          <Col className="work-assignment__left-sidebar" span={4}>
            <Select defaultValue="3/2020" onChange={e => console.log(e)}>
              <OptGroup label="2020">
                <Option value="1/2020">Tháng 1/2020</Option>
                <Option value="2/2020">Tháng 2/2020</Option>
                <Option value="3/2020">Tháng 3/2020</Option>
              </OptGroup>
            </Select>

            <div className="work-assignment__left-sidebar__list-tasks">
              <span className="work-assignment__left-sidebar__title">Danh sách công việc</span>
              <Input placeholder="Tìm kiếm công việc..." prefix={<SearchOutlined />} />
              <List
                size="small"
                bordered
                dataSource={tasks}
                renderItem={(item, index) => {
                  return (
                    <List.Item>
                      <Row>
                        <Col span={2}>
                          <span className="work-assignment__left-sidebar__list-tasks__task-index">
                            {index + 1}
                          </span>
                        </Col>
                        <Col span={22}>
                          <span className="work-assignment__left-sidebar__list-tasks__task-name">{item}</span>
                        </Col>
                      </Row>
                    </List.Item>
                  )
                }}
              />
            </div>

            <div className="work-assignment__left-sidebar__list-members">
              <List
                itemLayout="horizontal"
                dataSource={members}
                renderItem={item => (
                  <List.Item>
                    <List.Item.Meta
                      avatar={<Avatar src="https://zos.alipayobjects.com/rmsportal/ODTLcjxAfvqbxHnVXCYX.png" />}
                      title={item.title}
                      description="Đã tham gia lúc 00:36 14/03/2020"
                    />
                  </List.Item>
                )}
              />
            </div>

          </Col>
          <Col className="work-assignment__content" span={20}>
            <div className="work-assignment__content__header">
              <div className="work-assignment__content__header__task-name">
                <h3>Công việc 1</h3>
              </div>
              <div className="work-assignment__content__header__task-expiration">
                <span>01/03/2020 - 31/03/2020</span>
              </div>
            </div>
            <div className="work-assignment__content__body">
              <Tabs
                defaultActiveKey="1"
                tabBarGutter={50}
                className="work-assignment__content__body__tabs"
                onChange={e => console.log(e)}>
                <TabPane tab="Lịch làm việc" key="1">
                  Content of Tab Pane 1
                </TabPane>
                <TabPane tab="Mô tả công việc" key="2">
                  Content of Tab Pane 2
                </TabPane>
              </Tabs>
            </div>
          </Col>
        </Row>
      </div>
    )
  }
}
