import React, { Component } from 'react';
import { Card, Modal, List, Checkbox } from 'antd';
import { withCookies } from 'react-cookie';
import './WorkScheduleReport.style.scss';

const { Meta } = Card;

class WorkScheduleReport extends Component {
  constructor(props) {
    super(props);

    this.state = {
      isVisible: false
    }
  }

  setDialogVisible(isVisible) {
    this.setState({ isVisible });
  }

  printReport() {

  }

  render() {
    const { basicInfo, index, staffs } = this.props;

    return (
      <div className="reporting__work-schedule-report">
        <Card
          className={`reporting__menu__item animated fadeInUp`}
          style={{ width: '100%', animationDuration: `${0.5 * (index + 1)}s` }}
          cover={
            <img
              alt="example"
              src={basicInfo.cover}
            />
          }
          onClick={() => this.setDialogVisible(true)}
        >
          <Meta
            title={basicInfo.title}
            description={basicInfo.description}
          />
        </Card>

        <Modal
          title={basicInfo.title}
          visible={this.state.isVisible}
          onOk={() => this.printReport()}
          okButtonProps={{ style: { background: '#ff8220', border: 0 } }}
          onCancel={() => this.setDialogVisible(false)}
          cancelButtonProps={{ style: { display: 'none' } }}
          okText="In báo cáo"
        >
          <div className="reporting__work-schedule-report__content">
            <List
              itemLayout="horizontal"
              dataSource={staffs}
              renderItem={staff => (
                <List.Item>
                  <Row style={{ width: '100%' }} className="animated fadeIn">
                    <Col span={21}>
                      <List.Item.Meta
                        avatar={<Avatar src={staff.avatar} />}
                        title={staff.fullname}
                        description={USER_ROLES[staff.role].name}
                      />
                    </Col>
                    <Col span={3} className="add-work-shift-assigner__list-staffs__checkbox-wrapper">
                      <Checkbox />
                    </Col>
                  </Row>
                </List.Item>
              )}
            />
          </div>
        </Modal>

      </div>
    )
  }
}

export default withCookies(WorkScheduleReport);
