import React, { Component } from 'react';
import { Row, Col, Avatar, Dropdown, Menu } from 'antd';
import { LogoutOutlined } from '@ant-design/icons';
import { withCookies } from 'react-cookie';
import { withRouter } from 'react-router-dom';
import { COOKIE_NAMES } from '../../../constants/cookie-name.constant';
import { connect } from 'react-redux';
import * as actions from '../../../redux/actions';
import './Toolbar.style.scss';

class Toolbar extends Component {
  handleSelectMenuItem = (e, icon) => {
    const code = e.split('***');
    const key = code[0];
    const path = code[1];
    const title = code[2];

    if (key === 'LOGOUT') {
      this.logout();
      return;
    }

    this.props.setCurrentPageTitle(title, icon);
    this.props.history.push(path);
  }

  logout = () => {
    const { cookies } = this.props;
    cookies.remove(COOKIE_NAMES.token, { path: '/' });
    cookies.remove(COOKIE_NAMES.user, { path: '/' });
    this.props.history.push('/login');
  }

  render() {
    const { user, avatarMenuItems } = this.props;
    const { currentPageTitle, currentPageIcon } = this.props.app;
    const CurrentPage = { Icon: currentPageIcon };

    return (
      <Row className="toolbar" justify="start" align="middle">
        <Col className="__page-title" span={18}>
          <CurrentPage.Icon />
          <span className="__page-title__text">{currentPageTitle}</span>
        </Col>
        <Col span={6}>
          <Row className="__account" justify="center" align="middle">
            <Col span={14}>
              More user features here...
            </Col>
            <Col span={10}>
              <Dropdown overlay={
                <Menu onClick={e => this.handleSelectMenuItem(e.key, CurrentPage.Icon)}>
                  {(avatarMenuItems || []).map((item) => (
                    <Menu.Item key={item.key + '***' + item.path + '***' + item.title}>
                      <item.icon />
                      {item.title}
                    </Menu.Item>
                  ))}
                  <Menu.Item key="LOGOUT">
                    <LogoutOutlined />
                    Đăng xuất
                  </Menu.Item>
                </Menu>
              } className="__account__avatar-menu">
                <Row align="middle">
                  <Col span={7}><Avatar size="default" src={user.avatar} /></Col>
                  <Col span={17}><span>{user.fullname || ''}</span></Col>
                </Row>
              </Dropdown>
            </Col>
          </Row>
        </Col>
      </Row>
    )
  }
}

const mapStateToProps = state => ({
  app: state.app
})

export default connect(mapStateToProps, actions)(withCookies(withRouter(Toolbar)));
