import React, { Component } from 'react';
import { EditOutlined, EllipsisOutlined, SettingOutlined } from '@ant-design/icons';
import { Row, Col, Card } from 'antd';
import './Reporting.style.scss';

const { Meta } = Card;

const reports = [
  [
    {
      title: 'Lịch làm việc',
      cover: 'https://image.freepik.com/free-vector/flat-design-characters-doing-time-management_23-2148274068.jpg',
      description: 'Xuất báo cáo lịch làm việc trong tuần của một nhân viên.'
    },
    {
      title: 'Thống kê bán hàng',
      cover: 'https://www.wisertechsolutions.ca/wp-content/uploads/2019/05/ecom2.png',
      description: 'Xuất báo cáo lịch làm việc trong tuần của một nhân viên.'
    },
    {
      title: 'Báo cáo thu chi',
      cover: 'https://intuitionsoftech.com.au/wp-content/uploads/2019/09/it-home.png',
      description: 'Xuất báo cáo lịch làm việc trong tuần của một nhân viên.'
    }
  ]
]

export default class Reporting extends Component {
  render() {
    return (
      <div className="reporting">
        <div className="reporting__menu">
          {reports.map(row => (
            <Row justify="center" align="middle" gutter={[50, 50]} style={{ width: '100%' }}>
              {row.map((report, i) => (
                <Col span={8}>
                  <Card
                    className={`reporting__menu__item animated zoomIn`}
                    style={{ width: '100%', animationDuration: `${0.2 * (i + 1)}s` }}
                    cover={
                      <img
                        alt="example"
                        src={report.cover}
                      />
                    }
                    actions={[
                      <SettingOutlined key="setting" className="reporting__menu__item__icon" />,
                      <EditOutlined key="edit" className="reporting__menu__item__icon" />,
                      <EllipsisOutlined key="ellipsis" className="reporting__menu__item__icon" />,
                    ]}
                  >
                    <Meta
                      title={report.title}
                      description={report.description}
                    />
                  </Card>
                </Col>
              ))}
            </Row>
          ))}
        </div>
      </div>
    )
  }
}
