import React, { Component } from 'react';
import { PlusOutlined } from '@ant-design/icons';
import { Tooltip, Button, Modal, Row, Col } from 'antd';
import './ListStaffHeaderButtons.style.scss';

class ListStaffHeaderButtons extends Component {
  constructor(props) {
    super(props);

    this.state = {
      isAddUserDialogVisible: false
    }
  }

  setAddUserDialogVisible(isVisible) {
    this.setState({ isAddUserDialogVisible: isVisible });
  }

  onOk(dialogType) {
    switch (dialogType) {
      case 'ADD_USER': this.setAddUserDialogVisible(false); break;
      default: break;
    }
  }

  onCancel(dialogType) {
    switch (dialogType) {
      case 'ADD_USER': this.setAddUserDialogVisible(false); break;
      default: break;
    }
  }

  render() {
    return (
      <div className="staff-management__body__staffs__content__list-staffs__header__buttons">
        <Row>
          <Col>
            <Tooltip placement="bottom" title="Thêm nhân viên">
              <Button
                shape="circle"
                type="link"
                icon={<PlusOutlined />}
                onClick={() => this.setAddUserDialogVisible(true)}
              />
            </Tooltip>

            <Modal
              title="Vertically centered modal dialog"
              centered
              visible={this.state.isAddUserDialogVisible}
              onOk={() => this.onOk('ADD_USER')}
              onCancel={() => this.onCancel('ADD_USER')}
            >
              <p>some contents...</p>
              <p>some contents...</p>
              <p>some contents...</p>
            </Modal>
          </Col>
        </Row>
      </div>
    )
  }
}
export default ListStaffHeaderButtons;
