import React, { Component } from 'react';
import Geocode from "react-geocode";
import GoogleMapReact from 'google-map-react';
import { ShopFilled } from '@ant-design/icons';
import './GoogleMap.style.scss';
import { Tooltip } from 'antd';

const LocationIcon = ({ icon, text }) => (
  <div className="google-map__location-icon">
    <Tooltip title={text} placement="right">
      {icon}
    </Tooltip>
  </div>
);

const geocodeParams = {
  apiKey: 'AIzaSyDTkNdUuexN_PZOKtezyqWdO-g3-ewhHiQ',
  language: 'en',
  region: ''
}

const defaultLocation = { lat: 0, lng: 0 };
const defaultAddress = 'Unknown';

export default class GoogleMap extends Component {
  constructor(props) {
    super(props);
    this.state = {
      address: defaultAddress,
      location: defaultLocation
    }
  }

  async componentWillReceiveProps(props) {
    const { address } = props;
    const location = await this.getAddressLocation(address);
    const stardardAddress = await this.getAddressText(location.lat, location.lng);  
    this.setState({
      address: stardardAddress,
      location
    });
  }

  async getAddressText(latitude, longitude) {
    try {
      const res = await Geocode.fromLatLng(
        latitude,
        longitude,
        geocodeParams.apiKey,
        geocodeParams.language,
        geocodeParams.region
      );
      return res.results[0].formatted_address;
    } catch (error) {
      return defaultAddress;
    }
  }

  async getAddressLocation(address) {
    try {
      if (!address)
        return defaultLocation;

      const res = await Geocode.fromAddress(
        address,
        geocodeParams.apiKey,
        geocodeParams.language,
        geocodeParams.region
      );
      return res.results[0].geometry.location;
    } catch (error) {
      return defaultLocation;
    }
  }

  render() {
    const { width, height } = this.props;
    const { location, address } = this.state;

    if (location.lat === 0 && location.lng === 0) {
      return <></>;
    }

    console.log(address);

    return (
      <div className="google-map" style={{ width, height }}>
        <GoogleMapReact
          apiKey={geocodeParams.apiKey}
          defaultCenter={location}
          defaultZoom={17}
        >
          <LocationIcon
            lat={location.lat}
            lng={location.lng}
            icon={<ShopFilled />}
            text={address}
          />
        </GoogleMapReact>
      </div>
    )
  }
}
