import React, { Component } from 'react';
import Geocode from "react-geocode";
import GoogleMapReact from 'google-map-react';
import { ShopFilled } from '@ant-design/icons';
import './GoogleMap.style.scss';

const LocationIcon = ({ text, icon }) => (
  <div className="google-map__location-icon">
    {icon}
  </div>
);

const geocodeParams = {
  apiKey: 'AIzaSyDTkNdUuexN_PZOKtezyqWdO-g3-ewhHiQ',
  language: 'vn',
  region: 'Vietnam'
}

export default class GoogleMap extends Component {
  constructor(props) {
    super(props);
    this.state = {
      address: '',
      location: {}
    }
  }

  async getAddressText(longitude, latitude) {
    try {
      const res = await Geocode.fromLatLng(
        longitude,
        latitude,
        geocodeParams.apiKey,
        geocodeParams.language,
        geocodeParams.region
      );
      return res.results[0].formatted_address;
    } catch (error) {
      return null;
    }
  }

  async getAddressLocation(address) {
    try {
      const res = await Geocode.fromAddress(
        address,
        geocodeParams.apiKey,
        geocodeParams.language,
        geocodeParams.region
      );
      return res.results[0].geometry.location;
    } catch (error) {
      return {};
    }
  }

  async componentDidMount() {
    const address = await this.getAddressText("10.777919", "106.691867");
    const location = await this.getAddressLocation('31 Võ Văn Tần, Phường 6, Quận 3');
    this.setState({
      address,
      location
    });
  }

  render() {
    const { location, address } = this.state;

    if (Object.keys(location).length === 0)
      return <></>;

    return (
      <div style={{ width: 250, height: 250 }}>
        <GoogleMapReact
          apiKey={geocodeParams.apiKey}
          defaultCenter={location}
          defaultZoom={17}
        >
          <LocationIcon
            lat={location.lat}
            lng={location.lng}
            icon={<ShopFilled />}
          />
        </GoogleMapReact>
      </div>
    )
  }
}
