    "createdAt": "2020-03-18T11:59:19.234Z",
    "updatedAt": "2020-03-18T13:23:26.394Z",
    "__v": 3
  },
  {
    "workShifts": [
      {
        "workAssignments": [
          {
            "description": "Được phép nghỉ sớm 15 phút",
            "_id": "5e7236b3af071d55bbb38dc1",
            "assigner": "5e71d63e9e24116b1210d5d8",
            "manager": "5e6a530e9a59e035dc5e4e08",
