import React, { Component } from 'react';
import { Table } from 'antd';
import NumberFormat from 'react-number-format';

export default class BillToPrint extends Component {
  render() {
    const { checkoutSession } = this.props;
    const dataSource = checkoutSession.soldItems.map((item, index) => ({
      ...item,
      key: item.product._id,
      order: index + 1
    }))

    const columns = [
      {
        title: <center>STT</center>,
        dataIndex: 'order',
        key: 'order',
        render: order => <center>{order}</center>
      },
      {
        title: 'S.Phẩm',
        dataIndex: 'product',
        key: 'productName',
        render: product => product.name
      },
      {
        title: 'Đ.Giá',
        dataIndex: 'product',
        key: 'price',
        render: product => product.name
      },
      {
        title: 'SL',
        dataIndex: 'product',
        key: 'quantity',
        render: (product, record) => record.quantity
      },
      {
        title: 'T.Tiền',
        dataIndex: 'product',
        key: 'priceTotal',
        render: (product, record) => (
          <NumberFormat
            value={Number(product.price * record.quantity)}
            displayType="text"
            thousandSeparator={true}
            suffix=" VNĐ"
            style={{ fontWeight: 'bold' }}
          />
        )
      },
    ];

    return (
      <div className="money-counting__submit-checkout-session-dialog__bill">
        <Table dataSource={dataSource} columns={columns} />
      </div>
    )
  }
}
