import React, { Component } from 'react';
import { Row, Col, Tabs, Select, Input, List, Avatar, Button } from 'antd';
import { SearchOutlined, PlusCircleOutlined, CloseOutlined } from '@ant-design/icons';
import USER_ROLE from '../../../../constants/user-role.constant';
import './WorkAssignment.style.scss';
import * as monent from 'moment';
import WEEK_DAY from '../../../../constants/week-day.constant';

const { TabPane } = Tabs;
const { Option, OptGroup } = Select;

const tasks = [
  'Công việc 1',
  'Công việc 2',
  'Công việc 3',
  'Công việc 4',
  'Công việc 5',
];

const staffs = [
  {
    name: 'Tue Vo',
    role: USER_ROLE.MANAGER.name,
    avatar: 'tue.jpeg'
  },
  {
    name: 'Dung Pham',
    role: USER_ROLE.CASHIER.name,
    avatar: 'dung.jpg'
  },
  {
    name: 'Binh Nguyen',
    role: USER_ROLE.IMPORTER.name,
    avatar: 'binh.jpg'
  },
];

export default class WorkAssignment extends Component {
  constructor(props) {
    super(props);

    this.state = {
      taskWorkDays: [],
      isTaskWorkDayPanelShown: false
    }
  }

  toggleTaskWorkDayPanel() {
    const { isTaskWorkDayPanelShown } = this.state;
    this.setState({ isTaskWorkDayPanelShown: !isTaskWorkDayPanelShown });
  }

  createSchedule(month, year) {
    const workDays = monent(`${month}-${year}`, 'MM-YYYY').daysInMonth();
    let schedule = [];
    let temp = [];
    for (let i = 1; i <= workDays; i++) {
      if (i % 7 !== 0) {
        temp.push({
          workDayInMonth: i,
          workWeekDay: this.getWeekDay(i, month, year)
        });
      } else {
        temp.push({
          workDayInMonth: i,
          workWeekDay: this.getWeekDay(i, month, year)
        });
        schedule.push([...temp]);
        temp.length = 0;
      }
    }

    temp.length = 0;
    for (let i = 29; i <= workDays; i++) {
      temp.push({
        workDayInMonth: i,
        workWeekDay: this.getWeekDay(i, month, year)
      });
    }
    schedule.push([...temp]);
    temp.length = 0;

    return schedule;
  }

  getWeekDay(day, month, year) {
    const weekNumber = monent(`${day}-${month}-${year}`, 'DD-MM-YYYY').day();
    switch (weekNumber) {
      case 1: return WEEK_DAY.MONDAY;
      case 2: return WEEK_DAY.TUESDAY;
      case 3: return WEEK_DAY.WEDNESDAY;
      case 4: return WEEK_DAY.THURSDAY;
      case 5: return WEEK_DAY.FRIDAY;
      case 6: return WEEK_DAY.SATURDAY;
      case 7: return WEEK_DAY.SUNDAY;
      default: return null
    }
  }

  render() {
    let { taskWorkDays, isTaskWorkDayPanelShown } = this.state;
    taskWorkDays = [...this.createSchedule(3, 2020)];

    return (
      <div className="work-assignment">
        <Row>
          <Col className="work-assignment__left-sidebar animated slideInLeft" span={4}>
            <Select defaultValue="3/2020" onChange={e => console.log(e)}>
              <OptGroup label="Năm 2020">
                <Option value="1/2020">Tháng 1 năm 2020</Option>
                <Option value="2/2020">Tháng 2 năm 2020</Option>
                <Option value="3/2020">Tháng 3 năm 2020</Option>
              </OptGroup>
            </Select>

            <div className="work-assignment__left-sidebar__list-tasks">
              <span className="work-assignment__left-sidebar__title">Danh sách công việc</span>
              <Input
                className="work-assignment__left-sidebar__input"
                placeholder="Tìm kiếm công việc..."
                prefix={<SearchOutlined />} />
              <List
                size="small"
                bordered
                dataSource={tasks}
                renderItem={(item, index) => {
                  return (
                    <List.Item
                      className={
                        `work-assignment__left-sidebar__list-tasks__item 
                        ${index === 0 ? 'work-assignment__left-sidebar__list-tasks__item--selected' : ''}`
                      }>
                      <Row>
                        <Col span={24}>
                          <span className="work-assignment__left-sidebar__list-tasks__item__task-name">{item}</span>
                        </Col>
                      </Row>
                    </List.Item>
                  )
                }}
              />
              <Button
                className="work-assignment__left-sidebar__list-tasks__btn-add"
                icon={<PlusCircleOutlined />}>Thêm công việc</Button>
            </div>

            <div className="work-assignment__left-sidebar__list-staffs">
              <Row className="work-assignment__left-sidebar__title">
                <Col className="work-assignment__left-sidebar__title__text" span={22}><span>Nhân sự hiện có</span></Col>
                <Col className="work-assignment__left-sidebar__title__number" span={2}><span>(3)</span></Col>
              </Row>
              <Input
                className="work-assignment__left-sidebar__input"
                placeholder="Tìm kiếm nhân sự..."
                prefix={<SearchOutlined />} />
              <List
                itemLayout="horizontal"
                dataSource={staffs}
                renderItem={item => (
                  <List.Item>
                    <List.Item.Meta
                      avatar={<Avatar src={require(`../../../../assets/avatars/${item.avatar}`)} />}
                      title={item.name}
                      description={item.role}
                    />
                  </List.Item>
                )}
              />
            </div>

          </Col>
          <Col className="work-assignment__content animated animated fadeIn" span={20}>
            {isTaskWorkDayPanelShown ? (
              <div className="work-assignment__content__task-work-day-panel">
                <div className="work-assignment__content__task-work-day-panel__panel animated slideInRight">
                  <Button
                    className="work-assignment__content__task-work-day-panel__panel__btn-close"
                    shape="circle"
                    icon={<CloseOutlined />}
                    onClick={() => this.toggleTaskWorkDayPanel()} />
                </div>
              </div>
            ) : <></>}
            <div className="work-assignment__content__header">
              <div className="work-assignment__content__header__task-name">
                <h3>Công việc 1</h3>
              </div>
              <div className="work-assignment__content__header__task-expiration">
                <span>Tháng 3 năm 2020</span>
              </div>
            </div>
            <div className="work-assignment__content__body">
              <Tabs
                defaultActiveKey="1"
                tabBarGutter={50}
                className="work-assignment__content__body__tabs"
                onChange={e => console.log(e)}>
                <TabPane tab="Lịch làm việc" key="1" className="work-assignment__content__body__tabs__work-schedule">
                  {taskWorkDays.map((row, iRow) => (
                    <Row key={iRow + 1}>
                      {row.map((col) => (
                        <Col key={`${iRow + 1}_${col.workDayInMonth}`} span={3}
                          className="work-assignment__content__body__tabs__work-schedule__work-day"
                          onClick={() => this.toggleTaskWorkDayPanel()}>
                          {col.workDayInMonth}
                        </Col>
                      ))}
                    </Row>
                  ))}
                </TabPane>
                <TabPane tab="Mô tả công việc" key="2">
                  Content of Tab Pane 2
                </TabPane>
              </Tabs>
            </div>
          </Col>
        </Row>
      </div>
    )
  }
}
