import React, { Component } from 'react';
import { EditOutlined, EllipsisOutlined, SettingOutlined, CalendarOutlined, DollarCircleOutlined, InboxOutlined } from '@ant-design/icons';
import { Row, Col, Card } from 'antd';
import './Reporting.style.scss';

const { Meta } = Card;

const reports = [
  [
    { title: 'Lịch làm việc', icon: CalendarOutlined },
    { title: 'Sản phẩm bán ra/tồn kho', icon: InboxOutlined },
    { title: 'Báo cáo thu - chi', icon: DollarCircleOutlined }
  ]
]

export default class Reporting extends Component {
  render() {
    return (
      <div className="reporting">
        <div className="reporting__menu">
          {reports.map(row => (
            <Row justify="center" align="middle" gutter={[16, 16]} style={{ width: '100%' }}>
              {row.map(report => (
                <Col span={7}>
                  <Card
                    className="reporting__menu__item"
                    style={{ width: '100%' }}
                    cover={
                      <img
                        alt="example"
                        src="https://image.freepik.com/free-vector/flat-design-characters-doing-time-management_23-2148274068.jpg"
                      />
                    }
                    actions={[
                      <SettingOutlined key="setting" />,
                      <EditOutlined key="edit" />,
                      <EllipsisOutlined key="ellipsis" />,
                    ]}
                  >
                    <Meta
                      avatar={<report.icon className="reporting__menu__item__icon" />}
                      title={report.title}
                      description="This is the description"
                    />
                  </Card>
                </Col>
              ))}
            </Row>
          ))}
        </div>
      </div>
    )
  }
}
