import React, { Component } from 'react';
import { withCookies } from 'react-cookie';
import { Modal, Button, Tooltip, TimePicker, Row, Col, message } from 'antd';
import { PlusCircleFilled } from '@ant-design/icons';
// import { COOKIE_NAMES } from '../../../../../constants/cookie-name.constant';
import './BtnAddWorkShift.style.scss';
import moment from 'moment';

const format = 'HH:mm';

class BtnAddWorkShift extends Component {
  constructor(props) {
    super(props);
    this.state = {
      isVisible: false,
      startTime: null,
      endTime: null
    }
  }

  setModalVisible(isVisible) {
    this.setState({ isVisible });
  }

  onOK() {
    const {startTime, endTime} = this.state;
    if(!startTime || !endTime) {
      message.error('Vui lòng chọn đầy đủ các mốc thời gian');
      return;
    }
  }

  onCancel() {
    this.setModalVisible(false);
  }

  onChangeTimePicker(fieldName, e) {
    const time = new Date(e);
    const hours = time.getHours();
    const mins = time.getMinutes();

    const { selectedWorkShift } = this.props;
    if(!selectedWorkShift.startTime || !selectedWorkShift.endTime) {

    }

    switch (fieldName) {
      case 'startTime':
        const startTime = new Date(selectedWorkShift.startTime);
        startTime.setHours(hours);
        startTime.setMinutes(mins);
        console.log(moment(startTime).format('DD-MM-YYYY HH:mm'));
        break;

      case 'endTime':
        const endTime = new Date(selectedWorkShift.endTime);
        endTime.setHours(hours);
        endTime.setMinutes(mins);
        console.log(moment(endTime).format('DD-MM-YYYY HH:mm'));
        break;

      default:
        break;
    }
  }

  render() {
    const {
      workSchedules,
      selectedWorkSchedule,
      selectedWorkDay,
      selectedWorkShift
    } = this.props;

    return (
      <div className="add-work-shift-dialog">
        <Tooltip placement="bottom" title="Thêm ca làm việc">
          <Button
            onClick={() => this.setModalVisible(true)}
            className="add-work-shift-dialog__btn-open"
            type="link"
            icon={<PlusCircleFilled />} />
        </Tooltip>
        <Modal
          title={`${selectedWorkDay.workWeekDay ? selectedWorkDay.workWeekDay + ', ' : ''} ${selectedWorkDay.workYear ? moment(new Date(selectedWorkDay.workYear, selectedWorkDay.workMonth - 1, selectedWorkDay.workDayInMonth)).format('DD/MM/YYYY') + ' | Thêm ca làm việc trong ngày' : ''}`}
          centered
          visible={this.state.isVisible}
          onOk={() => this.onOK()}
          onCancel={() => this.onCancel()}
          okText="Thêm"
          cancelText="Hủy bỏ"
        >

          <div className="add-work-shift-dialog__time-picker">
            <Row align="center" className="add-work-shift-dialog__time-picker__time-selection">
              <Col span={8} className="add-work-shift-dialog__time-picker__time-selection__label" align="center">
                <span>Bắt đầu</span>
              </Col>
              <Col span={16}>
                <TimePicker
                  placeholder="Chọn mốc thời gian"
                  format={format}
                  onChange={e => this.onChangeTimePicker('startTime', e)}
                />
              </Col>
            </Row>

            <Row align="center" className="add-work-shift-dialog__time-picker__time-selection">
              <Col span={8} className="add-work-shift-dialog__time-picker__time-selection__label" align="center">
                <span>Kết thúc</span>
              </Col>
              <Col span={16}>
                <TimePicker
                  placeholder="Chọn mốc thời gian"
                  format={format}
                  onChange={e => this.onChangeTimePicker('endTime', e)}
                />
              </Col>
            </Row>
          </div>

        </Modal>
      </div>
    )
  }
}
export default withCookies(BtnAddWorkShift);
