import React, { Component } from 'react';
import './AddProductDialog.style.scss';
import { withCookies } from 'react-cookie';
import { connect } from 'react-redux';
import * as actions from '../../../../../redux/actions';
import { Button, Modal, Form, Input } from 'antd';
import { PlusOutlined } from '@ant-design/icons';
import ImageUploader from '../../../../utilities/ImageUploader/ImageUploader';

class AddProductDialog extends Component {
  constructor(props) {
    super(props);

    this.state = {
      isVisible: false
    }
  }

  setDialogVisible(isVisible) {
    this.setState({ isVisible });
  }

  async onOK() {

  }

  render() {
    const { selectedCategory } = this.props;
    return (
      <div className="product-management__add-product-dialog">
        <Button
          shape="circle"
          icon={<PlusOutlined />}
          className="product-management__add-product-dialog__btn-open"
          onClick={() => this.setDialogVisible(true)}
        />

        <div className="product-management__add-product-dialog__body">
          <Modal
            title={<span style={{ color: '#ff8220' }}>{`${selectedCategory.name} | Thêm sản phẩm mới`}</span>}
            centered
            visible={this.state.isVisible}
            onOk={() => this.onOK()}
            onCancel={() => this.setDialogVisible(false)}
            okText="Hoàn tất"
            cancelText="Hủy bỏ"
            okButtonProps={{ style: { background: '#ff8220', border: 0 } }}
          >
            <div className="product-management__add-product-dialog__body__content">
              <div className="product-management__add-product-dialog__body__content__img-uploading">
                <ImageUploader
                  width={150}
                  height={150}
                />
              </div>
              <Form>
                <Form.Item
                  name="name"
                  label="Tên sản phẩm"
                  rules={[{ required: true, message: 'Vui lòng nhập tên sản phẩm' }]}
                >
                  <Input placeholder="Tối đa 30 kí tự" />
                </Form.Item>
              </Form>
            </div>
          </Modal>
        </div>
      </div>
    )
  }
}
export default connect(null, actions)(withCookies(AddProductDialog));
