import React, { Component } from 'react';
import { PlusOutlined } from '@ant-design/icons';
import { Tooltip, Button, Modal, Row, Col } from 'antd';
import './ListStaffHeaderButtons.style.scss';

class ListStaffHeaderButtons extends Component {
  constructor(props) {
    super(props);

    this.state = {
      isAddUserDialogVisible: false
    }
  }

  setAddUserDialogVisible(isVisible) {
    this.setState({ isAddUserDialogVisible: isVisible });
  }

  render() {
    return (
      <div className="staff-management__body__staffs__content__list-staffs__header__buttons">
        <Row>
          <Col>
            <Tooltip placement="bottom" title="Thêm nhân viên">
              <Button
                shape="circle"
                type="link"
                icon={<PlusOutlined />}
                onClick={() => this.setAddUserDialogVisible(true)}
              />
            </Tooltip>
          </Col>
        </Row>
      </div>
    )
  }
}
export default ListStaffHeaderButtons;
