import React from 'react';
import { UsergroupAddOutlined } from '@ant-design/icons';
import { Button, Modal, Tooltip, message, List, Avatar, Row, Col, Checkbox } from 'antd';
import './BtnAddWorkAssigner.style.scss';
import { withCookies } from 'react-cookie';
import { COOKIE_NAMES } from '../../../../../constants/cookie-name.constant';
import { API } from '../../../../../constants/api.constant';
import PageBase from '../../../../utilities/PageBase/PageBase';
import USER_ROLES from '../../../../../constants/user-role.constant';

class BtnAddWorkAssigner extends PageBase {
  constructor(props) {
    super(props);

    this.state = {
      isVisible: false,
      staffs: [],
      assigners: [],
      isAllStaffsChecked: false
    }
  }

  componentDidMount() {
    this.loadStaffs();
  }

  loadStaffs = async () => {
    const res = await (
      await fetch(
        API.Manager.StaffManagement.getListStaffs,
        {
          method: 'GET',
          headers: {
            'Content-type': 'application/json; charset=UTF-8',
            'token': this.props.cookies.get(COOKIE_NAMES.token)
          },
          signal: this.abortController.signal
        }
      )
    ).json();

    if (res.status === 200) {
      const { users } = res.data;
      this.setState({ staffs: users.filter(u => u.role !== USER_ROLES.MANAGER.type) });
    } else {
      message.error(res.errors[0]);
    }
  }

  setModalVisible(isVisible) {
    this.setState({ isVisible });
  }

  onOK() {

  }

  onCancel() {
    this.setModalVisible(false);
  }

  onCheckStaff(staffID) {
    const { assigners } = this.state;
    assigners.push(staffID);
    this.setState({ assigners });
  }

  onCheckAllStaffs() {
    const { isAllStaffsChecked, staffs } = this.state;
    this.setState({ isAllStaffsChecked: !isAllStaffsChecked });

    const assigners = staffs.map(staff => staff._id);
    this.setState({ assigners });
  }

  render() {
    const { isBtnOpenDisabled } = this.props;
    const { staffs, assigners } = this.state;

    console.log(assigners);

    return (
      <div className="add-work-assigner">
        <Tooltip placement="bottom" title="Thêm nhân viên">
          <Button
            onClick={() => this.setModalVisible(true)}
            className="add-work-assigner__btn-open"
            type="link"
            icon={<UsergroupAddOutlined />}
            disabled={isBtnOpenDisabled}
          />
        </Tooltip>
        <Modal
          title="Phân công nhân viên"
          centered
          visible={this.state.isVisible}
          onOk={() => this.onOK()}
          onCancel={() => this.onCancel()}
          okText="Phân công"
          cancelText="Hủy bỏ"
        >

          <div className="add-work-assigner__list-staffs">
            <Row className="add-work-assigner__list-staffs__header">
              <Col span={21} className="add-work-assigner__list-staffs__header__col-1">
                <span>Nhân viên chưa được phân công</span>
              </Col>
              <Col span={3} className="add-work-assigner__list-staffs__header__col-2">
                <Checkbox onChange={() => this.onCheckAllStaffs()} />
              </Col>
            </Row>
            <List
              itemLayout="horizontal"
              dataSource={staffs}
              renderItem={staff => (
                <List.Item>
                  <Row style={{ width: '100%' }}>
                    <Col span={21}>
                      <List.Item.Meta
                        avatar={<Avatar src={staff.avatar} />}
                        title={staff.fullname}
                        description={USER_ROLES[staff.role].name}
                      />
                    </Col>
                    <Col span={3} className="add-work-assigner__list-staffs__checkbox-wrapper">
                      <Checkbox onChange={() => this.onCheckStaff(staff._id)} />
                    </Col>
                  </Row>
                </List.Item>
              )}
            />
          </div>

        </Modal>
      </div>
    )
  }
}
export default withCookies(BtnAddWorkAssigner);
