import React, { Component } from 'react';
import { Modal, Button, Calendar } from 'antd';
import { PlusCircleFilled } from '@ant-design/icons';
import './BtnAddWorkSchedule.style.scss';
import MONTHS from '../../../../../constants/months.constant';

export default class BtnAddWorkSchedule extends Component {
  constructor(props) {
    super(props);

    this.state = {
      isVisible: false,
      selectedDate: null
    }
  }

  setModalVisible(isVisible) {
    this.setState({ isVisible });
  }

  onOK() {
    const { selectedDate } = this.state;
    const month = selectedDate.getMonth() + 1;
    const year = selectedDate.getFullYear();
    console.log(month, year);
    this.setModalVisible(false);
  }

  onCancel() {
    console.log('Cancel');
    this.setModalVisible(false);
  }

  handleSelectMonth(e) {
    const selectedDate = new Date(e);
    this.setState({ selectedDate });
  }

  monthFullCellRender(date) {
    const { selectedDate } = this.state;
    const _date = new Date(date);
    const monthIndex = _date.getMonth();
    return (
      <span className={`
        add-work-schedule__calendar__month 
        ${selectedDate ? _date.getTime() === selectedDate.getTime() ? 'add-work-schedule__calendar__month--selected' : '' : ''}
      `}>
        {MONTHS[monthIndex]}
      </span>
    );
  }

  render() {
    return (
      <div className="add-work-schedule">
        <Button
          className="add-work-schedule__btn-add"
          icon={<PlusCircleFilled />}
          type="primary"
          onClick={() => this.setModalVisible(true)} >
          Thêm lịch làm việc
        </Button>
        <Modal
          title="Thêm lịch làm việc"
          centered
          visible={this.state.isVisible}
          onOk={() => this.onOK()}
          onCancel={() => this.onCancel()}
          okText="Thêm"
          cancelText="Hủy bỏ"
        >

          <div className="add-work-schedule__calendar">
            <Calendar
              mode="year"
              monthFullCellRender={date => this.monthFullCellRender(date)}
              fullscreen={false}
              onPanelChange={e => this.handleSelectMonth(e)} />
          </div>

        </Modal>
      </div>
    )
  }
}
