import React, { Component } from 'react';
import { Modal, Button, Calendar, message } from 'antd';
import { PlusCircleFilled } from '@ant-design/icons';
import './BtnAddWorkSchedule.style.scss';
import MONTHS from '../../../../../constants/months.constant';
import { API } from '../../../../../constants/api.constant';
import { withCookies } from 'react-cookie';
import { COOKIE_NAMES } from '../../../../../constants/cookie-name.constant';

class BtnAddWorkSchedule extends Component {
  constructor(props) {
    super(props);

    this.state = {
      isVisible: false,
      selectedDate: null,
      availableWorkSchedules: []
    }
  }

  componentWillMount() {
    const { availableWorkSchedules } = this.props;
    this.setState({ availableWorkSchedules });
  }

  setModalVisible(isVisible) {
    this.setState({ isVisible });
  }

  onOK = async () => {
    const { selectedDate } = this.state;
    if (!selectedDate) {
      message.error('Bạn chưa chọn tháng làm việc');
      return;
    }

    const month = selectedDate.getMonth() + 1;
    const year = selectedDate.getFullYear();
    const res = await (
      await fetch(
        API.Manager.WorkSchedule.addWorkSchedule,
        {
          method: 'POST',
          body: JSON.stringify({ month, year }),
          headers: {
            'Content-type': 'application/json; charset=UTF-8',
            'token': this.props.cookies.get(COOKIE_NAMES.token)
          }
        }
      )
    ).json();

    if (res.status === 200) {
      const { workSchedule } = res.data;
      console.log(workSchedule);
      message.success(res.messages[0]);
    } else {
      message.error(res.errors[0]);
    }
  }

  onCancel() {
    console.log('Cancel');
    this.setModalVisible(false);
  }

  handleSelectMonth(e) {
    const selectedDate = new Date(e);
    this.setState({ selectedDate });
  }

  monthFullCellRender(date) {
    const { selectedDate } = this.state;
    const _date = new Date(date);
    const monthIndex = _date.getMonth();
    return (
      <span className={`
        add-work-schedule__calendar__month 
        ${selectedDate ? _date.getTime() === selectedDate.getTime() ? 'add-work-schedule__calendar__month--selected' : '' : ''}
      `}>
        {MONTHS[monthIndex]}
      </span>
    );
  }

  render() {
    const { availableWorkSchedules } = this.state;
    console.log(availableWorkSchedules);

    return (
      <div className="add-work-schedule">
        <Button
          className="add-work-schedule__btn-add"
          icon={<PlusCircleFilled />}
          type="primary"
          onClick={() => this.setModalVisible(true)} >
          Thêm lịch làm việc
        </Button>
        <Modal
          title="Thêm lịch làm việc"
          centered
          visible={this.state.isVisible}
          onOk={() => this.onOK()}
          onCancel={() => this.onCancel()}
          okText="Thêm"
          cancelText="Hủy bỏ"
        >

          <div className="add-work-schedule__calendar">
            <Calendar
              mode="year"
              monthFullCellRender={date => this.monthFullCellRender(date)}
              fullscreen={false}
              onPanelChange={e => this.handleSelectMonth(e)} />
          </div>

        </Modal>
      </div>
    )
  }
}
export default withCookies(BtnAddWorkSchedule);
