import React, { Component } from 'react';
import { withCookies } from 'react-cookie';
import { connect } from 'react-redux';
import { Modal, Form, Input, Button, message } from 'antd';
import './EditCategoryDialog.style.scss';

const layout = {
  labelCol: { span: 6 },
  wrapperCol: { span: 18 },
};

class EditCategoryDialog extends Component {
  constructor(props) {
    super(props);
    this.state = {
      isVisible: false
    }
    this.formRef = React.createRef();
  }

  setDialogVisible(isVisible) {
    this.setState({ isVisible });
  }

  onOk() {
    document.getElementById('edit-category-dialog-btn-submit').click();
  }
  onFinish(values) {
    console.log(values);
  }

  render() {
    return (
      <div className="product-management__edit-category-dialog">
        <Button type="link" className="product-management__edit-category-dialog__btn-open">Chỉnh sửa</Button>
        <Modal
          className="product-management__edit-category-dialog__content"
          title={<span style={{ color: '#ff8220' }}>Chỉnh sửa thông tin</span>}
          centered
          visible={this.state.isVisible}
          onOk={() => this.onOk()}
          onCancel={() => {
            if (this.formRef.current) {
              this.formRef.current.resetFields();
            }
            this.setDialogVisible(false);
          }}
          okText="Hoàn tất"
          cancelText="Hủy bỏ"
          okButtonProps={{ style: { background: '#ff8220', border: 0 } }}
        >
          <Form
            {...layout}
            ref={current => {
              this.formRef.current = current;
              if(this.formRef.current) {
                this.formRef.current.setFieldValues({
                  name: this.props.category.name
                });
              }
            }}
            onFinish={values => this.onFinish(values)}
            onFinishFailed={() => message.error('Chưa nhập đầy đủ thông tin, vui lòng kiểm tra lại.')}
          >
            <Form.Item
              name="name"
              label="Tên danh mục"
              rules={[{ required: true, message: 'Vui lòng nhập tên danh mục' }]}
            >
              <Input placeholder="Tối đa 30 kí tự" autoFocus={true} />
            </Form.Item>
            <Form.Item style={{ display: 'none' }}>
              <Button id="edit-category-dialog-btn-submit" htmlType="submit" />
            </Form.Item>
          </Form>
        </Modal>
      </div>
    )
  }
}
export default connect(null, EditCategoryDialog)(withCookies(EditCategoryDialog));
