import React, { Component } from 'react';
import { Form, Input, Button } from 'antd';
import { LockFilled } from '@ant-design/icons'
import './ChangePassword.style.scss';

const layout = {
  labelCol: {
    span: 8,
  },
  wrapperCol: {
    span: 16,
  },
};
const tailLayout = {
  wrapperCol: {
    offset: 8,
    span: 16,
  },
};

export default class ChangePassword extends Component {

  onFinish = values => {
    console.log('Success:', values);
  }

  onFinishFailed = errorInfo => {
    console.log('Failed:', errorInfo);
  }

  render() {
    return (
      <div className="change-password">
        <div className="change-password__content">
          <div className="change-password__content__div-icon">
            <div className="change-password__content__div-icon__icon-wrapper">
              <LockFilled className="change-password__content__div-icon__icon-wrapper__icon" />
            </div>
          </div>
          <Form
            {...layout}
            onFinish={values => this.onFinish(values)}
            onFinishFailed={() => this.onFinishFailed()}
            className="change-password__content__form"
          >
            <Form.Item
              label="Mật khẩu hiện tại"
              name="currentPassword"
              rules={[
                {
                  required: true,
                  message: 'Vui lòng nhập mật khẩu hiện tại',
                },
              ]}
            >
              <Input.Password autoFocus={true} />
            </Form.Item>

            <Form.Item
              label="Mật khẩu mới"
              name="newPassword"
              rules={[
                {
                  required: true,
                  message: 'Vui lòng nhập mật khẩu mới',
                },
              ]}
            >
              <Input.Password />
            </Form.Item>

            <Form.Item
              label="Nhập lại mật khẩu mới"
              name="confirmedNewPassword"
              rules={[
                {
                  required: true,
                  message: 'Vui lòng nhập lại mật khẩu mới',
                },
              ]}
            >
              <Input.Password />
            </Form.Item>

            <Form.Item {...tailLayout}>
              <Button htmlType="submit" className="change-password__content__form__btn-submit">
                Đổi mật khẩu
              </Button>
            </Form.Item>
          </Form>
        </div>
      </div>
    )
  }
}
