import React, { Component } from 'react';
import { withCookies } from 'react-cookie';
import { Button, Modal, Tooltip, Input, Table, Checkbox } from 'antd';
import { RocketOutlined, SearchOutlined } from '@ant-design/icons';
import './SupplierDialog.style.scss';
import moment from 'moment';
import * as _ from 'lodash';

class SupplierDialog extends Component {
  _isMount = false;
  constructor(props) {
    super(props);

    this.state = {
      isVisible: false,
      suppliers: [],
      filteredSuppliers: [],
      supplierSearchText: '',
      checkAllSuppliers: false
    }
  }

  componentWillMount() {
    this._isMount = false;
  }

  componentDidMount() {
    this._isMount = true;

    if (this._isMount) {
      let { suppliers } = this.props;
      console.log(suppliers);

      suppliers = suppliers
        .map(s => ({ ...s, key: s._id, checked: false }))
        .filter(s => s._id !== 'ALL')
        .map((s, i) => ({ ...s, order: i + 1 }));

      for (let i = 0; i < 20; i++)
        suppliers.push(suppliers[0]);

      this.setState({ suppliers });
    }
  }

  setDialogVisible(isVisible) {
    this.setState({ isVisible });
  }

  onOk() {

  }

  handleCheckAllSuppliers(checked) {
    this.setState({ checkAllSuppliers: checked });
  }

  handleCheckSupplier(supplierID, checked) {
    const index = _.findIndex(this.props.suppliers, s => s._id === supplierID);
    if (index < 0)
      return;

    this.props.suppliers[index].checked = checked;
    console.log(this.props.suppliers);
  }

  render() {
    const { suppliers } = this.state;
    const columns = [
      {
        title: 'STT',
        dataIndex: 'order',
        key: 'order',
        width: 80
      },
      {
        title: 'Thương hiệu',
        dataIndex: 'name',
        key: 'name',
      },
      {
        title: 'Số điện thoại',
        dataIndex: 'phone',
        key: 'phone',
      },
      {
        title: 'Địa chỉ',
        dataIndex: 'address',
        key: 'address',
      },
      {
        title: 'Ngày tạo',
        dataIndex: 'createdAt',
        key: 'createdAt',
        render: (createdAt) => moment(createdAt).format('DD-MM-YYYY HH:mm')
      },
      {
        title: (
          <Checkbox
            style={{ marginLeft: 3 }}
            defaultChecked={this.state.checkAll}
            onChange={e => this.handleCheckAllSuppliers(e.target.checked)}
          />
        ),
        dataIndex: 'checked',
        key: 'checked',
        render: (checked, supplier) => (
          <Checkbox
            defaultChecked={checked}
            onChange={e => this.handleCheckSupplier(supplier._id, e.target.checked)}
          />
        )
      }
    ];

    return (
      <div className="product-management__supplier-dialog">
        <Tooltip title="Nhà cung cấp sản phẩm" placement="left">
          <Button
            shape="circle"
            icon={<RocketOutlined />}
            className="product-management__supplier-dialog__btn-open"
            onClick={() => this.setDialogVisible(true)}
          />
        </Tooltip>

        <Modal
          className="product-management__supplier-dialog__content"
          title={
            <div className="product-management__supplier-dialog__content__title">
              <div className="product-management__supplier-dialog__content__title__icon-wrapper">
                <RocketOutlined className="product-management__supplier-dialog__content__title__icon-wrapper__icon" />
              </div>
              <span className="product-management__supplier-dialog__content__title__text">Nhà cung cấp sản phẩm</span>
            </div>
          }
          visible={this.state.isVisible}
          onCancel={() => this.setDialogVisible(false)}
          okButtonProps={{ style: { display: 'none' } }}
          cancelButtonProps={{ style: { display: 'none' } }}
        >
          <div className="product-management__supplier-dialog__content__toolbar">
            <Input prefix={<SearchOutlined />} placeholder="Tìm kiếm nhà cung cấp" />
          </div>

          <div className="product-management__supplier-dialog__content__suppliers">
            <Table
              dataSource={[...suppliers]}
              columns={columns}
              pagination={false}
              scroll={{ y: 300 }}
            />
          </div>

        </Modal>
      </div>
    )
  }
}
export default withCookies(SupplierDialog);
