import React from 'react';
import './MoneyCounting.style.scss';
import PageBase from '../../../utilities/PageBase/PageBase';
import { withCookies } from 'react-cookie';
import QrReader from 'react-qr-reader';
import { Row, Col, Empty, message } from 'antd';
import NumberFormat from 'react-number-format';

class MoneyCounting extends PageBase {
  constructor(props) {
    super(props);
    this.state = {
      scannedProduct: null
    }
  }

  handleScan = data => {
    if (data) {
      this.setState({
        scannedProduct: JSON.parse(data)
      })
    }
  }

  handleError = error => {
    message.error(error);
  }

  render() {
    const { scannedProduct } = this.state;

    return (
      <div className="money-counting">
        <div className="money-counting__panel">
          <Row style={{ width: '100%', height: '100%' }}>
            <Col span={7}>
              <div className="money-counting__panel__left">
                <div className="money-counting__panel__left__product-scanning">
                  <QrReader
                    delay={300}
                    onError={this.handleError}
                    onScan={this.handleScan}
                    className="money-counting__panel__left__product-scanning__scanner"
                  />
                </div>
                <div className="money-counting__panel__left__product-details">
                  <h3>Thông tin sản phẩm</h3>
                  {scannedProduct ? (
                    <Row style={{ width: '100%', height: '100%' }} gutter={20}>
                      <Col span={8}>
                        <ul className="money-counting__panel__left__product-details__labels">
                          <li>Tên sản phẩm</li>
                          <li>Giá bán</li>
                          <li>Nhà phân phối</li>
                        </ul>
                      </Col>
                      <Col span={16}>
                        <ul className="money-counting__panel__left__product-details__texts">
                          <li>{scannedProduct.name}</li>
                          <li>
                            <NumberFormat
                              value={scannedProduct.price}
                              displayType="text"
                              thousandSeparator={true}
                              suffix=" VNĐ"
                              style={{ fontWeight: 'bold' }} />
                          </li>
                          <li>{scannedProduct.supplier.name}</li>
                        </ul>
                      </Col>
                    </Row>
                  ) : (<Empty />)}
                </div>
              </div>
            </Col>
            <Col span={17}>
              <div className="money-counting__panel__right">
                <div className="money-counting__panel__right__topbar"></div>
                <div className="money-counting__panel__right__shopping-cart"></div>
                <div className="money-counting__panel__right__toolbar"></div>
              </div>
            </Col>
          </Row>
        </div>

      </div>
    )
  }
}
export default withCookies(MoneyCounting)
