import React from 'react';
import { Button, Modal } from 'antd';
import { RightOutlined } from '@ant-design/icons';
import './SubmitCheckoutSessionDialog.style.scss';
import PageBase from '../../../../utilities/PageBase/PageBase';
import { connect } from 'react-redux';
import { withCookies } from 'react-cookie';
import * as actions from '../../../../../redux/actions';
import moment from 'moment';

class SubmitCheckoutSessionDialog extends PageBase {
  constructor(props) {
    super(props);
    this.state = {
      isVisible: false,
      checkoutSession: {}
    }
  }

  setDialogVisible(isVisible) {
    this.setState({ isVisible });
  }

  onOK() {

  }

  async submitCheckoutSession() {
    const { checkoutSessionID } = this.props;
    const res = {
      "status": 200,
      "data": {
        "checkoutSession": {
          "soldItems": [
            {
              "quantity": 3,
              "_id": "5e89c58a21db4e34190a43f4",
              "product": {
                "name": "Sữa bò tươi có đường",
                "price": 8000,
                "availableQuantity": 1,
                "_id": "5e70f8e809d06d1841aea689"
              },
              "checkoutSession": "5e89c57b21db4e34190a43f3",
              "createdAt": "2020-04-05T11:48:26.618Z",
              "updatedAt": "2020-04-05T11:48:26.618Z",
              "__v": 0
            },
            {
              "quantity": 1,
              "_id": "5e89c58a21db4e34190a43f6",
              "product": {
                "name": "Sữa ADM Gold",
                "price": 40000,
                "availableQuantity": 6,
                "_id": "5e84abd73866907a1d35da6d"
              },
              "checkoutSession": "5e89c57b21db4e34190a43f3",
              "createdAt": "2020-04-05T11:48:26.618Z",
              "updatedAt": "2020-04-05T11:48:26.618Z",
              "__v": 0
            },
            {
              "quantity": 2,
              "_id": "5e89c58a21db4e34190a43f5",
              "product": {
                "name": "Dao nhập khẩu Châu Âu",
                "price": 1250000,
                "availableQuantity": 6,
                "_id": "5e85a87a242b435251cdacb0"
              },
              "checkoutSession": "5e89c57b21db4e34190a43f3",
              "createdAt": "2020-04-05T11:48:26.618Z",
              "updatedAt": "2020-04-05T11:48:26.618Z",
              "__v": 0
            }
          ],
          "priceTotal": 2564000,
          "submittedAt": "2020-04-05T11:48:26.902Z",
          "_id": "5e89c57b21db4e34190a43f3",
          "cashier": "5e80295b917650122d2e5d17",
          "createdAt": "2020-04-05T11:48:11.800Z",
          "updatedAt": "2020-04-05T11:48:26.904Z",
          "__v": 1
        }
      },
      "messages": [
        "Hoàn tất phiên tính tiền"
      ]
    };

    const { checkoutSession } = res.data;
    this.setState({ checkoutSession });

    this.setDialogVisible(true);
  }

  render() {
    const { checkedOutProducts } = this.props;
    const { checkoutSession } = this.state;
    return (
      <div className="money-counting__submit-checkout-session-dialog">
        <Button
          className="money-counting__submit-checkout-session-dialog__btn-open"
          type="primary"
          disabled={checkedOutProducts.length === 0}
          onClick={() => this.submitCheckoutSession()}
        >
          <span style={{ marginRight: 5 }}>Xuất hóa đơn</span>
          <RightOutlined />
        </Button>

        <Modal
          title={
            <span style={{ color: '#ff8220', fontWeight: 'bold' }}>
              Hóa đơn bán hàng | {moment(checkoutSession.submittedAt).format('HH:mm DD-MM-YYYY')}
            </span>
          }
          visible={this.state.isVisible}
          onOk={() => this.onOK()}
          onCancel={() => this.setDialogVisible(false)}
          okText="In hóa đơn"
          okButtonProps={{ style: { background: '#ff8220', border: 0, fontWeight: 'bold' } }}
          cancelButtonProps={{ style: { display: 'none' } }}
        >
          {Object.keys(checkoutSession).length > 0 ? (
            <ul>
              {checkoutSession.soldItems.map(item => (
                <li>{item.product.name}</li>
              ))}
            </ul>
          ) : <></>}
        </Modal>
      </div>
    )
  }
}
export default connect(null, actions)(withCookies(SubmitCheckoutSessionDialog));
