import React, { Component } from 'react';
import './ProductManagement.style.scss';
import { Row, Col, Input, List, Button, Table } from 'antd';
import { SearchOutlined, PlusOutlined, PlusCircleFilled, CloseOutlined } from '@ant-design/icons';

const categories = [
  {
    name: 'Sữa tiệt trùng'
  },
  {
    name: 'Dụng cụ làm bếp'
  },
  {
    name: 'Văn phòng phẩm'
  },
  {
    name: 'Đồ gia dụng'
  }
];

const dataSource = [
  {
    key: '1',
    name: 'Mike',
    age: 32,
    address: '10 Downing Street',
  },
