import React, { Component } from 'react';
import { EditOutlined, EllipsisOutlined, SettingOutlined, CalendarOutlined, DollarCircleOutlined, InboxOutlined } from '@ant-design/icons';
import { Row, Col, Card } from 'antd';
import './Reporting.style.scss';

const { Meta } = Card;

const reports = [
  [
    {
      title: 'Lịch làm việc',
      icon: CalendarOutlined,
      thumbnail: 'https://image.freepik.com/free-vector/flat-design-characters-doing-time-management_23-2148274068.jpg',
      description: 'Xuất báo cáo lịch làm việc của nhân viên trong tuần.'
    },
    { title: 'Thống kê bán hàng', icon: InboxOutlined, thumbnail: 'https://www.wisertechsolutions.ca/wp-content/uploads/2019/05/ecom2.png' },
    { title: 'Báo cáo thu chi', icon: DollarCircleOutlined, thumbnail: 'https://intuitionsoftech.com.au/wp-content/uploads/2019/09/it-home.png' }
  ]
]

export default class Reporting extends Component {
  render() {
    return (
      <div className="reporting">
        <div className="reporting__menu">
          {reports.map(row => (
            <Row justify="center" align="middle" gutter={[50, 50]} style={{ width: '100%' }}>
              {row.map((report, i) => (
                <Col span={8}>
                  <Card
                    className={`reporting__menu__item animated fadeInUp`}
                    style={{ width: '100%', animationDuration: `${0.5 * (i + 1)}s` }}
                    cover={
                      <img
                        alt="example"
                        src={report.thumbnail}
                      />
                    }
                    actions={[
                      <SettingOutlined key="setting" />,
                      <EditOutlined key="edit" />,
                      <EllipsisOutlined key="ellipsis" />,
                    ]}
                  >
                    <Meta
                      avatar={<report.icon className="reporting__menu__item__icon" />}
                      title={report.title}
                      description={report.description}
                    />
                  </Card>
                </Col>
              ))}
            </Row>
          ))}
        </div>
      </div>
    )
  }
}
