import React, { Component } from 'react';
import { withCookies } from 'react-cookie';
import { EditOutlined } from '@ant-design/icons';
import { Skeleton, Row, Col, Select, Form, Modal, Input, message, DatePicker, Button, Avatar } from 'antd';
import './UpdateStaffDialog.style.scss';
import USER_ROLES from '../../../../../constants/user-role.constant';
import USER_SEX from '../../../../../constants/user-sex';

const { Option } = Select;
const layout = {
  labelCol: { span: 6 },
  wrapperCol: { span: 18 },
};

class UpdateStaffDialog extends Component {
  constructor(props) {
    super(props);

    this.state = {
      isVisible: false
    }
  }

  setDialogVisible(isVisible) {
    this.setState({ isVisible });
  }

  onOk() {
    document.getElementById('btn-submit').click();
  }

  onCancel() {
    this.setDialogVisible(false);
  }

  onFinish = async (values) => {
  }

  render() {
    const { selectedStaff } = this.props;

    return (
      <div>
        {selectedStaff ? (
          <div>
            <Row align="middle">
              <Col span={2}>
                <EditOutlined className="staff-management__body__staffs__sidebar__staff-features__feature__icon" />
              </Col>
              <Col
                span={22}
                className="staff-management__body__staffs__sidebar__staff-features__feature__info"
                onClick={() => this.setDialogVisible(true)}
              >
                <span className="staff-management__body__staffs__sidebar__staff-features__feature__info__name">
                  Chỉnh sửa thông tin</span>
              </Col>
            </Row>

            <Modal
              className="staff-management__body__staffs__content__list-staffs__header__dialogs__update-staff-dialog__content"
              title={
                <div>
                  <Avatar size={30} src={selectedStaff.avatar} style={{ marginRight: 10 }} />
                  <span style={{ color: '#ff8220' }}>{selectedStaff.fullname} | Thông tin cá nhân</span>
                </div>
              }
              centered
              visible={this.state.isVisible}
              onOk={() => this.onOk()}
              onCancel={() => this.onCancel()}
              okText="Cập nhật"
              cancelText="Hủy bỏ"
              okButtonProps={{ style: { background: '#ff8220', borderColor: '#ff8220' } }}
            >

              <Form
                {...layout}
                initialValues={{
                  fullname: selectedStaff.fullname,
                  email: selectedStaff.email
                }}
                className="staff-management__body__staffs__content__list-staffs__header__dialogs__update-staff-dialog__form"
                onFinish={e => this.onFinish(e)}
                onFinishFailed={() => { message.error('Thông tin cung cấp chưa đầy đủ. Vui lòng kiểm tra lại !'); }}
              >
                <Form.Item style={{ display: 'none' }}>
                  <Button type="primary" htmlType="submit" id="btn-submit" />
                </Form.Item>

                <Form.Item
                  label="Họ và tên"
                  name="fullname"
                  rules={[
                    {
                      required: true,
                      message: 'Vui lòng nhập họ và tên'
                    }
                  ]}
                >
                  <Input placeholder="Tối đa 30 kí tự" />
                </Form.Item>

                <Form.Item
                  label="Chức vụ"
                  name="role"
                  rules={[
                    {
                      required: true,
                      message: 'Vui lòng chọn chức vụ'
                    }
                  ]}
                >
                  <Select
                    placeholder="Chọn chức vụ"
                    allowClear
                  >
                    <Option value={USER_ROLES.CASHIER.type}>{USER_ROLES.CASHIER.name}</Option>
                    <Option value={USER_ROLES.IMPORTER.type}>{USER_ROLES.IMPORTER.name}</Option>
                    <Option value={USER_ROLES.MANAGER.type}>{USER_ROLES.MANAGER.name}</Option>
                  </Select>
                </Form.Item>

                <Form.Item
                  label="Giới tính"
                  name="sex"
                  rules={[
                    {
                      required: true,
                      message: 'Vui lòng chọn giới tính'
                    }
                  ]}
                >
                  <Select
                    placeholder="Chọn giới tính"
                    allowClear
                  >
                    <Option value={USER_SEX.MALE.type}>{USER_SEX.MALE.name}</Option>
                    <Option value={USER_SEX.FEMALE.type}>{USER_SEX.FEMALE.name}</Option>
                  </Select>
                </Form.Item>

                <Form.Item
                  label="Ngày sinh"
                  name="dateOfBirth"
                  rules={[
                    {
                      required: true,
                      message: 'Vui lòng chọn ngày sinh'
                    }
                  ]}
                >
                  <DatePicker placeholder="Chọn ngày sinh" format="DD-MM-YYYY" />
                </Form.Item>

                <Form.Item
                  label="E-mail"
                  name="email"
                  rules={[
                    {
                      required: true,
                      message: 'Vui lòng nhập địa chỉ email'
                    },
                    {
                      type: "email",
                      message: 'Địa chỉ email không hợp lệ'
                    }
                  ]}
                >
                  <Input placeholder="abc@gmail.com" />
                </Form.Item>

                <Form.Item
                  label="Điện thoại"
                  name="phone"
                >
                  <Input maxLength={11} placeholder="Gồm 10 hoặc 11 số" />
                </Form.Item>

                <Form.Item
                  label="Địa chỉ"
                  name="address"
                >
                  <Input.TextArea placeholder="227 Nguyễn Văn Cừ, Quận 5" />
                </Form.Item>

                <Form.Item
                  label="Ảnh đại diện"
                  name="avatar"
                >
                  <Input placeholder="Dán đường dẫn ảnh vào đây" />
                </Form.Item>

              </Form>

            </Modal>

          </div>
        ) : (<Skeleton.Input style={{ width: '88%', height: 20 }} active={true} size="small" />)}
      </div>
    )
  }
}
export default withCookies(UpdateStaffDialog);
