import React from 'react';
import { withCookies } from 'react-cookie';
import './EditingDialog.style.scss';
import { Modal } from 'antd';
import PageBase from '../../../../../utilities/PageBase/PageBase';

class EditingPanel extends PageBase {
  constructor(props) {
    super(props);
    this.state = {
      isVisible: false
    }
  }

  setVisible(isVisible) {
    this.setState({ isVisible });
  }

  onOk() {

  }

  render() {
    return (
      <div className="product-management__supplier-dialog__editing-dialog">
        <span onClick={() => this.setVisible(true)}>Chỉnh sửa</span>
        <Modal
          className="product-management__supplier-dialog__editing-dialog__content"
          title={<span style={{ color: '#ff8220', fontWeight: 'bold' }}></span>}
          visible={this.state.isVisible}
          onOk={() => this.onOk()}
          onCancel={() => this.setVisible(false)}
          okText="Lưu thay đổi"
          cancelText="Hủy bỏ"
          okButtonProps={{ style: { background: '#ff8220', border: 0 } }}
        >
          ...
        </Modal>
      </div>
    )
  }
}
export default withCookies(EditingPanel);
