import React, { Component } from 'react';
import { CalendarOutlined } from '@ant-design/icons';
import { Row, Col } from 'antd';
import './Reporting.style.scss';

const reports = [
  [{ title: 'Work schedule', icon: CalendarOutlined }, { title: 'Work schedule', icon: CalendarOutlined }, { title: 'Work schedule', icon: CalendarOutlined }],
  [{ title: 'Work schedule', icon: CalendarOutlined }, { title: 'Work schedule', icon: CalendarOutlined }, { title: 'Work schedule', icon: CalendarOutlined }]
]

export default class Reporting extends Component {
  render() {
    return (
      <div className="reporting">
        <div className="reporting__menu">
          {reports.map(row => (
            <Row justify="center" align="middle" style={{ width: '100%' }}>
              {row.map(report => (
                <Col span={8} align="middle">
                  <div className="reporting__menu__item">
                    <report.icon className="reporting__menu__item__icon" />
                    <h3 className="reporting__menu__item__title">{report.title}</h3>
                  </div>
                </Col>
              ))}
            </Row>
          ))}
        </div>
      </div>
    )
  }
}
