import React from 'react';
import './MoneyCounting.style.scss';
import PageBase from '../../../utilities/PageBase/PageBase';
import { withCookies } from 'react-cookie';
import QrReader from 'react-qr-reader';
import { Row, Col, Empty, notification, Button, Tooltip, Table } from 'antd';
import { ReloadOutlined, ShoppingCartOutlined } from '@ant-design/icons';
import NumberFormat from 'react-number-format';
import soundfile from '../../../../assets/sounds/cashing.wav';
// import moment from 'moment';

const dataSource = [
  {
    key: '1',
    name: 'Mike',
    age: 32,
    address: '10 Downing Street',
  },
  {
    key: '2',
    name: 'John',
    age: 42,
    address: '10 Downing Street',
  },
  {
    key: '3',
    name: 'John',
    age: 42,
    address: '10 Downing Street',
