import React, { Component } from 'react';
import { Modal, Table } from 'antd';
import { ExclamationCircleFilled } from '@ant-design/icons';
import './ImportingRequestDialog.style.scss';

const dataSource = [
  {
    key: '1',
    product: {
      name: 'Bộ dao Shiramiku',
      image: ''
    },
    requiredQuantity: 3,
    availableQuantity: 0,
  },
  {
    key: '2',
    product: {
      name: 'Dao nhập khẩu Châu Âu',
      image: ''
    },
    requiredQuantity: 2,
    availableQuantity: 1,
  }
];

export default class ImportingRequestDialog extends Component {
  constructor(props) {
    super(props);
    this.state = {
      isVisible: true
    }
  }

  setDialogVisible(isVisible) {
    this.setState({ isVisible });
  }

  onOk() {

  }

  render() {

    const columns = [
      {
        title: 'Sản phẩm',
        dataIndex: 'productName',
        key: 'productName',
        render: (value, record) => record.product.name
      },
      {
        title: 'SL yêu cầu',
        dataIndex: 'requiredQuantity',
        key: 'requiredQuantity',
      },
      {
        title: 'SL hiện có',
        dataIndex: 'availableQuantity',
        key: 'availableQuantity',
      },
    ];

    return (
      <div className="importing-request-dialog">
        <Modal
          className="importing-request-dialog__content"
          title={
            <div className="importing-request-dialog__content__title">
              <ExclamationCircleFilled className="importing-request-dialog__content__title__icon" />
              <span className="importing-request-dialog__content__title__text">Sản phẩm không đủ</span>
            </div>
          }
          visible={this.state.isVisible}
          footer={null}
        >
          <Table
            dataSource={dataSource}
            columns={columns}
            pagination={false}
          />
        </Modal>
      </div>
    )
  }
}
