import React, { Component } from 'react';
import { Layout, Menu } from 'antd';
import { Link, withRouter } from 'react-router-dom';
import './Sidebar.style.scss';
import UserInfo from './UserInfo/UserInfo';
import { connect } from 'react-redux';
import * as actions from '../../../redux/actions';
import links from '../../../constants/sidebar.constant';
import USER_ROLE from '../../../constants/user-role.constant';

const { Sider } = Layout;

class Sidebar extends Component {
  constructor(props) {
    super(props);
    this.state = {
      currentPagePath: '',
      currentPageIndex: -1
    }
  }

  componentWillMount() {
    const { pagesByUserRole } = this.props;
    const href = window.location.href.split('?')[0];
    const position = href.split('/');
    const currentPath = position[position.length - 1];

    let currentPageIndex, currentPagePath;
    pagesByUserRole.forEach((page, index) => {
      if (page.path === `/${currentPath}`) {
        currentPageIndex = index;
        const currentPage = pagesByUserRole[currentPageIndex];
        currentPagePath = currentPage.path;
        this.props.setCurrentPageTitle(currentPage.title, currentPage.icon);

        if (!this.isCommonPage(page.path))
          this.props.setSidebarSelection(true);
        else this.props.setSidebarSelection(false);
      }
    });

    if (!this.pageIndex) {
      currentPageIndex = 0;
      const { title, icon, path } = pagesByUserRole[currentPageIndex];
      currentPagePath = path;
      this.props.setCurrentPageTitle(title, icon);

      if (!this.isCommonPage(path))
        this.props.setSidebarSelection(true);
      else this.props.setSidebarSelection(false);

      this.props.history.push(path);
    }

    this.setState({ currentPageIndex, currentPagePath });
  }

  isCommonPage(path) {
    const page = links.find(link => link.role === USER_ROLE.USER.role).pages.find(page => page.path === path);
    if (page)
      return true;
    return false;
  }

  render() {
    const { user, pagesByUserRole } = this.props;
    let { currentPageIndex } = this.state;
    const { isSidebarSelected } = this.props.app;

    if (!isSidebarSelected)
      currentPageIndex = null;
    else currentPageIndex = currentPageIndex.toString();

    console.log(currentPageIndex);

    return (
      <Sider className="sidebar">
        <UserInfo user={user} />
        <Menu theme='light' mode='inline' defaultSelectedKeys={[currentPageIndex]}>
          {pagesByUserRole.map((page, pageIndex) => {
            const Page = { Icon: page.icon };
            if (!this.isCommonPage(page.path))
              return (
                <Menu.Item key={pageIndex} className="animated slideInLeft"
                  onClick={() => {
                    this.props.setCurrentPageTitle(page.title, page.icon);
                    this.props.setSidebarSelection(true);
                  }}>
                  <Page.Icon />
                  <span className='nav-text'>{page.title}</span>
                  <Link to={page.path} />
                </Menu.Item>
              )
            return null;
          })}
        </Menu>
      </Sider>
    )
  }
}

const mapStateToProps = state => ({
  app: state.app
});

export default connect(mapStateToProps, actions)(withRouter(Sidebar));
