import React, { Component } from 'react';
import Geocode from "react-geocode";

const geocodeParams = {
  apiKey: 'AIzaSyDTkNdUuexN_PZOKtezyqWdO-g3-ewhHiQ',
  language: 'vn',
  region: 'Vietnam'
}

Geocode.fromAddress(
  "31 Võ Văn Tần, Phường 6, Quận 3",
  geocodeParams.apiKey,
  geocodeParams.language,
  geocodeParams.region
).then(
  res => {
    console.log(res);
  },
  err => {
    console.log(err);
  }
)

export default class GoogleMap extends Component {

  /**
   * @summary Get the address's text by its longitude and latitude.
   * @param {string} longitude - Address's longitude.
   * @param {string} latitude - Address's latitude.
   */
  async getAddressText(longitude, latitude) {
    const res = await Geocode.fromLatLng(
      longitude,
      latitude,
      geocodeParams.apiKey,
      geocodeParams.language,
      geocodeParams.region
    );

    console.log(res.results[0].geometry.location);
  }

  render() {
    this.getAddressText("10.777919", "106.691867");
    return (
      <div>
        GoogleMap
      </div>
    )
  }
}
