import React, { Component } from 'react';
import { Avatar, Row, Col } from 'antd';
import { withCookies } from 'react-cookie';
import { COOKIE_NAMES } from '../../../../constants/cookie-name.constant';
import USER_ROLES from '../../../../constants/user-role.constant';
import './Profile.style.scss';

class Profile extends Component {
  render() {
    const user = this.props.cookies.get(COOKIE_NAMES.user);
    const userFields = Object.keys(user)
      .filter(k => !['fullname', 'avatar', 'role', 'updatedAt', '__v'].includes(k))
      .map(k => {
        let _field;
        switch (k) {
          case '_id': _field = { index: 0, name: 'ID' }; break;
          case 'dateOfBirth': _field = { index: 1, name: 'Ngày sinh' }; break;
          case 'sex': _field = { index: 2, name: 'Giới tính' }; break;
          case 'email': _field = { index: 3, name: 'email' }; break;
          case 'phone': _field = { index: 4, name: 'Số điện thoại' }; break;
          case 'address': _field = { index: 5, name: 'Địa chỉ' }; break;
          case 'salaryRate': _field = { index: 6, name: 'Hệ số lương' }; break;
          case 'createdAt': _field = { index: 7, name: 'Ngày tham gia' }; break;
          default: _field = { name: '' }; break;
        }
        _field.value = user[_field.name];
        return _field;
      })
      .sort((a, b) => a.index - b.index);

    return (
      <div className="profile">
        <div className="profile__main animated fadeInUp">
          <div className="profile__main__header">
            <Avatar
              className="profile__main__header__user-avatar"
              size={70}
              src="https://spon.mdp.ac.id/pluginfile.php/1/theme_moove/marketing1icon/1582524735/icon-lecture.png" />
            <div className="profile__main__header__user-info">
              <div className="profile__main__header__user-info__name"><span>{user.fullname}</span></div>
              <div className="profile__main__header__user-info__role"><span>{USER_ROLES[user.role].name}</span></div>
            </div>
          </div>
          <Row className="profile__main__body">
            <Col span={8}>
              <ul>
                {userFields.map((field, index) => (
                  <li key={index}>{field.name}</li>
                ))}
              </ul>
            </Col>
            <Col span={16}>
              <ul>
                {userFields.map((field, index) => (
                  <li key={index}>{field.value}</li>
                ))}
              </ul>
            </Col>
          </Row>
        </div>
      </div>
    )
  }
}

export default withCookies(Profile);
