import React, { Component } from 'react';
import { withCookies } from 'react-cookie';
import { Button, Modal, Tooltip, Input, Table } from 'antd';
import { RocketOutlined, SearchOutlined } from '@ant-design/icons';
import './SupplierDialog.style.scss';

class SupplierDialog extends Component {
  constructor(props) {
    super(props);
    this.state = {
      isVisible: false,
      suppliers: [],
      filteredSuppliers: [],
      searchText: ''
    }
  }

  componentDidMount() {
    let { suppliers } = this.props;
    suppliers = suppliers.filter(s => s._id !== 'ALL');
    this.loadSuppliers(suppliers);
    this.setState({ filteredSuppliers: suppliers });
  }

  loadSuppliers(suppliersDataSource) {
    let suppliers = [...suppliersDataSource];
    suppliers = suppliers.map(s => ({ ...s, key: s._id }));
    this.setState({ suppliers });
  }

  setDialogVisible(isVisible) {
    this.setState({ isVisible });
  }

  onOk() {

  }

  render() {
    const { filteredSuppliers } = this.state;
    console.log(filteredSuppliers);
    const columns = [
      {
        title: 'Thương hiệu',
        dataIndex: 'name',
        key: 'name',
      },
      {
        title: 'Số điện thoại',
        dataIndex: 'phone',
        key: 'phone',
      },
      {
        title: 'Địa chỉ',
        dataIndex: 'address',
        key: 'address',
      }
    ];

    return (
      <div className="product-management__supplier-dialog">
        <Tooltip title="Nhà cung cấp sản phẩm" placement="left">
          <Button
            shape="circle"
            icon={<RocketOutlined />}
            className="product-management__supplier-dialog__btn-open"
            onClick={() => this.setDialogVisible(true)}
          />
        </Tooltip>

        <Modal
          className="product-management__supplier-dialog__content"
          title={<span style={{ color: '#ff8220' }}>Nhà cung cấp sản phẩm</span>}
          visible={this.state.isVisible}
          onCancel={() => this.setDialogVisible(false)}
          okButtonProps={{ style: { display: 'none' } }}
          cancelButtonProps={{ style: { display: 'none' } }}
        >
          <div className="product-management__supplier-dialog__content__toolbar">
            <Input prefix={<SearchOutlined />} placeholder="Tìm kiếm nhà cung cấp" />
          </div>

          <div className="product-management__supplier-dialog__content__suppliers">
            <Table
              dataSource={[...filteredSuppliers]}
              columns={columns}
              pagination={false}
            />
          </div>

        </Modal>
      </div>
    )
  }
}
export default withCookies(SupplierDialog);
