import React from 'react';
import { UsergroupAddOutlined } from '@ant-design/icons';
import { Button, Modal, Tooltip, message, List, Avatar, Row, Col, Checkbox } from 'antd';
import './BtnAddWorkShiftAssigner.style.scss';
import { withCookies } from 'react-cookie';
import { COOKIE_NAMES } from '../../../../../constants/cookie-name.constant';
import { API } from '../../../../../constants/api.constant';
import PageBase from '../../../../utilities/PageBase/PageBase';
import USER_ROLES from '../../../../../constants/user-role.constant';

class BtnAddWorkShiftAssigner extends PageBase {
  constructor(props) {
    super(props);

    this.state = {
      isVisible: false,
      staffs: [],
      assigners: [],
      isAllStaffsChecked: false,
      selectedWorkShift: {}
    }
  }

  componentDidMount() {
    this.loadStaffs();
  }

  loadStaffs = async () => {
    const res = await (
      await fetch(
        API.Manager.StaffManagement.getListStaffs,
        {
          method: 'GET',
          headers: {
            'Content-type': 'application/json; charset=UTF-8',
            'token': this.props.cookies.get(COOKIE_NAMES.token)
          },
          signal: this.abortController.signal
        }
      )
    ).json();

    if (res.status === 200) {
      const { users } = res.data;
      this.setState({ staffs: users.filter(u => u.role !== USER_ROLES.MANAGER.type) });
    } else {
      message.error(res.errors[0]);
    }
  }

  setModalVisible(isVisible) {
    if (isVisible) {
      const { selectedWorkShift } = this.props;
      this.setState({ selectedWorkShift });

      let { staffs } = this.state;
      staffs = staffs.filter(s => !(selectedWorkShift.assigners || []).find(a => a._id === s._id));
      this.setState({ staffs });

      console.log(staffs);
    }

    this.setState({ isVisible });
  }

  onOK() {

  }

  onCancel() {
    this.setModalVisible(false);
  }

  onCheckStaff(e, staffID) {
    const { checked } = e.target;
    let { assigners, staffs } = this.state;

    if (checked) {
      assigners.push(staffID);

      if (assigners.length === staffs.length) {
        this.setState({ isAllStaffsChecked: true })
      }
    } else {
      assigners = assigners.filter(id => id !== staffID);

      if (assigners.length < staffs.length) {
        this.setState({ isAllStaffsChecked: false })
      }
    }

    this.setState({ assigners });
  }

  onCheckAllStaffs(e) {
    const { checked } = e.target;
    const { isAllStaffsChecked, staffs } = this.state;
    this.setState({ isAllStaffsChecked: !isAllStaffsChecked });

    let { assigners } = this.state;
    if (checked) {
      assigners = staffs.map(staff => staff._id);
    } else {
      assigners = [];
    }

    this.setState({ assigners });
  }

  render() {
    let { staffs, assigners } = this.state;

    return (
      <div className="add-work-shift-assigner">
        <Tooltip placement="bottom" title="Thêm nhân viên">
          <Button
            onClick={() => this.setModalVisible(true)}
            className="add-work-shift-assigner__btn-open"
            type="link"
            icon={<UsergroupAddOutlined />}
            disabled={this.props.isBtnOpenDisabled}
          />
        </Tooltip>
        <Modal
          title="Phân công nhân viên"
          centered
          visible={this.state.isVisible}
          onOk={() => this.onOK()}
          onCancel={() => this.onCancel()}
          okText="Phân công"
          cancelText="Hủy bỏ"
        >

          <div className="add-work-shift-assigner__list-staffs">
            <Row className="add-work-shift-assigner__list-staffs__header">
              <Col span={21} className="add-work-shift-assigner__list-staffs__header__col-1">
                <span>Nhân viên chưa được phân công</span>
              </Col>
              <Col span={3} className="add-work-shift-assigner__list-staffs__header__col-2">
                <Checkbox onChange={e => this.onCheckAllStaffs(e)} checked={this.state.isAllStaffsChecked} />
              </Col>
            </Row>
            <List
              itemLayout="horizontal"
              dataSource={staffs}
              renderItem={staff => (
                <List.Item>
                  <Row style={{ width: '100%' }}>
                    <Col span={21}>
                      <List.Item.Meta
                        avatar={<Avatar src={staff.avatar} />}
                        title={staff.fullname}
                        description={USER_ROLES[staff.role].name}
                      />
                    </Col>
                    <Col span={3} className="add-work-shift-assigner__list-staffs__checkbox-wrapper">
                      <Checkbox
                        onChange={e => this.onCheckStaff(e, staff._id)}
                        checked={assigners.find(id => id === staff._id)} />
                    </Col>
                  </Row>
                </List.Item>
              )}
            />
          </div>

        </Modal>
      </div>
    )
  }
}
export default withCookies(BtnAddWorkShiftAssigner);
