import React, { Component } from 'react';
import { PlusOutlined } from '@ant-design/icons';
import { Tooltip, Button, Modal, Form, Input, Select, DatePicker, Divider } from 'antd';
import './AddStaffDialog.style.scss';
import USER_SEX from '../../../../../constants/user-sex';

const { Option } = Select;

const layout = {
  labelCol: { span: 6 },
  wrapperCol: { span: 18 },
};

class AddStaffDialog extends Component {
  constructor(props) {
    super(props);

    this.state = {
      isVisible: false
    }
  }

  setDialogVisible(isVisible) {
    this.setState({ isVisible });
  }

  onOk() {
    this.setDialogVisible(false);
  }

  onCancel() {
    this.setDialogVisible(false);
  }

  render() {
    return (
      <div className="staff-management__body__staffs__content__list-staffs__header__dialogs__add-staff-dialog">
        <Tooltip placement="bottom" title="Thêm nhân viên">
          <Button
            className="staff-management__body__staffs__content__list-staffs__header__dialogs__add-staff-dialog__btn-open"
            shape="circle"
            type="link"
            icon={<PlusOutlined />}
            onClick={() => this.setDialogVisible(true)}
          />
        </Tooltip>

        <Modal
          title="Thêm nhân viên mới"
          centered
          visible={this.state.isVisible}
          onOk={() => this.onOk()}
          onCancel={() => this.onCancel()}
        >

          <Form
            {...layout}
            className="staff-management__body__staffs__content__list-staffs__header__dialogs__add-staff-dialog__form">

            <Divider orientation="left">Thông tin cá nhân</Divider>

            <Form.Item
              label="Họ và tên"
              name="fullname"
              rules={[
                {
                  required: true,
                  message: 'Vui lòng nhập họ và tên'
                }
              ]}
            >
              <Input placeholder="Tối đa 30 kí tự" />
            </Form.Item>

            <Form.Item
              label="Giới tính"
              name="sex"
              rules={[
                {
                  required: true,
                  message: 'Vui lòng chọn giới tính'
                }
              ]}
            >
              <Select
                placeholder="Chọn giới tính"
                allowClear
              >
                <Option value={USER_SEX.MALE.type}>{USER_SEX.MALE.name}</Option>
                <Option value={USER_SEX.FEMALE.type}>{USER_SEX.FEMALE.name}</Option>
              </Select>
            </Form.Item>

            <Form.Item
              label="Ngày sinh"
              name="dateOfBirth"
              rules={[
                {
                  required: true,
                  message: 'Vui lòng chọn ngày sinh'
                }
              ]}
            >
              <DatePicker placeholder="Chọn ngày sinh" />
            </Form.Item>

            <Form.Item
              label="E-mail"
              name="email"
              rules={[
                {
                  required: true,
                  message: 'Vui lòng nhập địa chỉ email'
                }
              ]}
            >
              <Input placeholder="abc@gmail.com" />
            </Form.Item>

            <Form.Item
              label="Điện thoại"
              name="phone"
              rules={[
                {
                  required: true,
                  message: 'Vui lòng nhập số điện thoại'
                }
              ]}
            >
              <Input placeholder="Gồm 10 hoặc 11 số" />
            </Form.Item>

            <Divider orientation="left">Thông tin tài khoản</Divider>

          </Form>

        </Modal>
      </div>
    )
  }
}
export default AddStaffDialog;
