import React from 'react';
import { withCookies } from 'react-cookie';
import './EditSupplierDialog.style.scss';
import { Modal, Button, Form, Input } from 'antd';
import PageBase from '../../../../../utilities/PageBase/PageBase';

const layout = {
  labelCol: { span: 4 },
  wrapperCol: { span: 20 },
};

class EditSupplierDialog extends PageBase {
  constructor(props) {
    super(props);
    this.state = {
      isVisible: false
    }
  }

  setVisible(isVisible) {
    this.setState({ isVisible });
  }

  onOk() {

  }

  render() {
    const { supplier } = this.props;

    return (
      <div className="product-management__supplier-dialog__editing-dialog">
        <Button
          type="link"
          className="product-management__supplier-dialog__editing-dialog__btn-open"
          onClick={() => this.setVisible(true)}
        >Chỉnh sửa</Button>
        <Modal
          className="product-management__supplier-dialog__editing-dialog__content"
          title={<span style={{ color: '#ff8220', fontWeight: 'bold' }}>{`Nhà cung cấp: ${supplier.name} | Sửa thông tin`}</span>}
          visible={this.state.isVisible}
          centered
          onOk={() => this.onOk()}
          onCancel={() => this.setVisible(false)}
          okText="Lưu thay đổi"
          cancelText="Hủy bỏ"
          okButtonProps={{ style: { background: '#ff8220', border: 0 } }}
        >
          <Form
            {...layout}
            ref={current => { this.frmAddSupplierRef.current = current; }}
            onFinish={values => this.submitFrmAddSupplier(values)}
            onFinishFailed={() => message.error('Chưa đầy đủ thông tin, vui lòng kiểm tra lại.')}
          >
            <Form.Item
              name="name"
              label="Tên thương hiệu"
              rules={[{ required: true, message: 'Vui lòng nhập tên thương hiệu' }]}
            >
              <Input placeholder="Tối đa 50 kí tự" autoFocus={true} />
            </Form.Item>
            <Form.Item
              name="phone"
              label="Số điện thoại"
              rules={[{ required: true, message: 'Vui lòng nhập số điện thoại' }]}
            >
              <Input placeholder="Gồm 10 hoặc 11 số" maxLength={11} />
            </Form.Item>
            <Form.Item
              name="address"
              label="Địa chỉ liên hệ"
              rules={[{ required: true, message: 'Vui lòng nhập địa chỉ' }]}
            >
              <Input.TextArea placeholder="Tối đa 200 kí tự" />
            </Form.Item>
            <Form.Item {...tailLayout}>
              <Button
                type="primary"
                htmlType="submit"
                className="product-management__supplier-dialog__content__frm-add-supplier__btn-submit"
              >
                Thêm
                </Button>
            </Form.Item>
          </Form>
        </Modal>
      </div>
    )
  }
}
export default withCookies(EditSupplierDialog);
