import React, { Component } from 'react';
import GooglePlacesAutocomplete from 'react-google-places-autocomplete';
import 'react-google-places-autocomplete/dist/index.min.css';
import './GooglePlacesAutocomplete.style.scss';
import { Input } from 'antd';
import { geocodeByAddress } from 'react-google-places-autocomplete';

/**
 * REMEMBER: Enable Maps Javascript API & Places API from Google Cloud Flatform
 */
export default class PlacesAutocomplete extends Component {
  constructor(props) {
    super(props);
    this.inputRef = React.createRef();
  }

  async handleSelectPlace(e) {
    const { description } = e;
    const res = await geocodeByAddress(description);
    const placeInfo = {
      address: description,
      location: {
        lat: res[0].geometry.location.lat(),
        lng: res[0].geometry.location.lng()
      }
    };
    this.props.validateAddress(true, placeInfo);
  }

  render() {
    return (
      <div className="google-places-autocomplete">
        <GooglePlacesAutocomplete
          placeholder="Nhập địa chỉ..."
          suggestionsClassNames={{ suggestion: 'google-places-autocomplete__suggestions' }}
          onSelect={e => this.handleSelectPlace(e)}
          renderInput={(props) => (
            <div>
              <Input
                {...props}
                ref={current => {
                  this.inputRef.current = current;
                  if (this.inputRef.current) {
                    this.props.validateAddress(false, undefined);
                    this.inputRef.current.state.value = '';
                  }
                }}
              />
              {!this.inputRef.current && this.inputRef.current.state.value ? (
                <span className="google-places-autocomplete__error">Vui lòng chọn địa chỉ</span>
              ) : <></>}
            </div>
          )}
        />
      </div>
    )
  }
}
