import React from 'react';
import PageBase from '../../../utilities/PageBase/PageBase';
import './StaffManagement.style.scss';
import { SearchOutlined, UserAddOutlined } from '@ant-design/icons';
import { Input, Row, Col, Select, Table, Button, Avatar } from 'antd';

const { Option } = Select;

const columns = [
  {
    title: 'Name',
    dataIndex: 'name',
    width: 150,
  },
  {
    title: 'Age',
    dataIndex: 'age',
    width: 150,
  },
  {
    title: 'Address',
    dataIndex: 'address',
  },
];

const data = [];
for (let i = 0; i < 100; i++) {
  data.push({
    key: i,
    name: `Edward King ${i}`,
    age: 32,
    address: `London, Park Lane no. ${i}`,
  });
}

export default class StaffManagement extends PageBase {
  render() {
    return (
      <div className="staff-management">
        <div className="staff-management__body">
          <Row className="staff-management__body__staffs">
            <Col span={5}>
              <div className="staff-management__body__staffs__sidebar">
                <div className="staff-management__body__staffs__sidebar__staff-info">
                  <Avatar
                    className="profile__main__header__user-avatar"
                    size={70}
                    src="https://spon.mdp.ac.id/pluginfile.php/1/theme_moove/marketing1icon/1582524735/icon-lecture.png" />
                  <div className="profile__main__header__user-info">
                    <div className="profile__main__header__user-info__name"><span>{user.fullname}</span></div>
                    <div className="profile__main__header__user-info__role"><span>{USER_ROLES[user.role].name}</span></div>
                  </div>
                </div>
              </div>
            </Col>
            <Col span={19}>
              <div className="staff-management__body__staffs__content">
                <Row className="staff-management__body__staffs__content__toolbar">
                  <Col span={10}>
                    <Input prefix={<SearchOutlined style={{ marginRight: 5 }} />} placeholder="Tìm kiếm nhân viên..." />
                  </Col>
                </Row>
                <div className="staff-management__body__staffs__content__list-staffs">
                  <div className="staff-management__body__staffs__content__list-staffs__header">
                    <div className="staff-management__body__staffs__content__list-staffs__header__dark-bg"></div>
                    <Row>
                      <Col span={6} >
                        <div className="staff-management__body__staffs__content__list-staffs__header__role-selection">
                          <Select defaultValue="CASHIER" style={{ width: 220 }}>
                            <Option value="CASHIER">Nhân viên thu ngân</Option>
                            <Option value="IMPORTER">Nhân viên nhập hàng</Option>
                          </Select>
                        </div>
                      </Col>
                      <Col span={18}>
                        <div className="staff-management__body__staffs__content__list-staffs__header__buttons">
                          <Button shape="circle" type="link" icon={<UserAddOutlined />} />
                        </div>
                      </Col>
                    </Row>
                  </div>
                  <Row className="staff-management__body__staffs__content__list-staffs__wrapper">
                    <Table columns={columns} dataSource={data} pagination={{ pageSize: 50 }} scroll={{ y: 340 }} />
                  </Row>
                </div>
              </div>
            </Col>
          </Row>
        </div>
      </div>
    )
  }
}
